// 在此处定义常量和辅助函数

float hash(vec2 p) {
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

vec2 hash2(vec2 p) {
    vec2 h = vec2(hash(p), hash(p + vec2(1.0)));
    return -1.0 + 2.0 * h;
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(dot(hash2(i + vec2(0.0, 0.0)), f - vec2(0.0, 0.0)),
                   dot(hash2(i + vec2(1.0, 0.0)), f - vec2(1.0, 0.0)), u.x),
               mix(dot(hash2(i + vec2(0.0, 1.0)), f - vec2(0.0, 1.0)),
                   dot(hash2(i + vec2(1.0, 1.0)), f - vec2(1.0, 1.0)), u.x), u.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float time = iTime * 2.;
    float radius = 0.3;
    float thickness = 0.05;

    // 计算到中心的距离
    float dist = length(uv);

    // 创建基本环形
    float ring = smoothstep(radius - thickness, radius, dist) - 
                 smoothstep(radius, radius + thickness, dist);

    // 添加流动效果
    vec2 noiseCoord = vec2(atan(uv.y+1., uv.x) * 2.0, dist * 1.0) + time;
    float flow = noise(noiseCoord) * 2.5 + 1.5;

    // 将流动效果应用到环形上
    ring *= flow;

    // 设置颜色
    vec3 color = vec3(0.2, 0.5, 0.8) * ring;

    fragColor = vec4(color, 1.0);
}
