Main {
    Q = C(U)/float(iFrame);
    //Q = A(U);
    //Q = abs(B(U).zzzz*.01);

}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.14159265359
vec2 proj (vec2 U, vec2 a, vec2 b) {
    return a+(b-a)*clamp(dot(U-a,b-a)/dot(b-a,b-a),0.,1.);
}
float ln (vec2 U, vec2 a, vec2 b) {

    return length(U-proj(U,a,b));

}
bool inside (vec2 U, vec2 a, vec2 b, vec2 c) {
    return dot(U-proj(a,b,c),proj(a,b,c)-a) <=0.&&
           dot(U-proj(b,c,a),proj(b,c,a)-b) <=0.&&
           dot(U-proj(c,a,b),proj(c,a,b)-c) <=0.;
}

#define N 20.
Main {
    for (float x = -2.; x <= 2.; x++) 
    for (float y = -2.; y <= 2.; y++) {
       vec2 U0 = U;
       U.y *= sqrt(1.25);
       U.x += .5*U.y;
       U += vec2(x,y)*N*.125;
       vec2 a = floor((U)/N)*N;
       vec2 b = a + N*vec2(1,0);
       vec2 c = a + N*vec2(1,1);
       if ((U.x - a.x) < (U.y-a.y)) {
           b = a + vec2(0,1)*N;
       }

       a.x -= .5*a.y;
       a.y /= sqrt(1.25);
       b.x -= .5*b.y;
       b.y /= sqrt(1.25);
       c.x -= .5*c.y;
       c.y /= sqrt(1.25);
       U = U0;
       vec4 ha = hash44(round(vec4(a,a)));
       vec4 hb = hash44(round(vec4(b,b)));
       vec4 hc = hash44(round(vec4(c,c)));

        ha.x = 3.*pow(ha.x,1.);
       hb.x = 3.*pow(hb.x,1.);
       hc.x = 3.*pow(hc.x,1.);

        //ha.x *= (.5+.5*cos(50.*dot((a.xy-.5*R)/R.y,(a.xy-.5*R)/R.y)))*exp(-3.*length(a.xy-.5*R)/R.y);
        //hb.x *= (.5+.5*cos(50.*dot((b.xy-.5*R)/R.y,(b.xy-.5*R)/R.y)))*exp(-3.*length(b.xy-.5*R)/R.y);
        //hc.x *= (.5+.5*cos(50.*dot((c.xy-.5*R)/R.y,(c.xy-.5*R)/R.y)))*exp(-3.*length(c.xy-.5*R)/R.y);

       a.xy += .15*N*(ha.zw*2.-1.);
       b.xy += .15*N*(hb.zw*2.-1.);
       c.xy += .15*N*(hc.zw*2.-1.);



       if (inside(U,a,b,c))

       Q = 
           ha*(ln(U,b,c)/ln(a,b,c))+
           hb*(ln(U,c,a)/ln(b,c,a))+
           hc*(ln(U,a,b)/ln(c,a,b));


   }



}

Main {
    if (iFrame > 1) {Q = B(U);return;}
    vec3 p = vec3(.5*R,-.5*R.y);
    vec3 d = normalize(vec3(2.*(U-.5*R)/R.y,1));
    p.yz *= ei(.2);
    d.yz *= ei(.2);
    for (float i = 0.; i < 300.; i++) {
        vec4 q = A(p.xy);
        p += .2*d*-(p.z-30.*q.x);

    }
    Q.xyz = p;

}

Main {
    Q = C(U);
    vec4 h = hash44(vec4(U,iTime,iFrame));
    U += .5*(h.zw*2.-1.);
    float a = h.x*2.*pi;
    float b = h.y*pi;
    vec3 d = vec3(vec2(cos(a),sin(a))*sin(b),cos(b));
    vec4 q = A(U);
    vec3 o = normalize(vec3(2.*(U-.5*R)/R.y,1));
    o.yz *= ei(.2);
    vec3 p = B(U).xyz;
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.x-w.x,n.x-s.x,1./30.));
    d.z = abs(d.z);
    d = normalize(reflect(o,no)+d);
    p += d*1.;
    vec3 light = vec3(.5*R.x,1.*R.y,1.5*R.y);
    for (float i = 0.; i < 100.; i++) {
        vec4 q = A(p.xy);
        float m = min(p.z-30.*q.x,length(p-light)-200.);
        p += .5*d*m;
        if (m > R.x) break;
        if (m < .001) {
            vec4 n = A(p.xy+vec2(0,1));
            vec4 e = A(p.xy+vec2(1,0));
            vec4 s = A(p.xy-vec2(0,1));
            vec4 w = A(p.xy-vec2(1,0));
            vec3 no = normalize(vec3(e.x-w.x,n.x-s.x,1./30.));
            d = reflect(d,no);
            p += .5*d;
        }
    }
    Q += .5*exp(-10.*dot(d.xy,d.xy))+15.*(exp(-.1*(length(p-light)-200.)))*max(sin(.02*(p.x-light.x)+vec4(1,2,3,4)),0.);

}
