    vec3 a = vec3(0.0, 0.0, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(5.163,5.216,0.257);
      vec3 f = vec3(0.5+cos(iTime),-0.5+cos(iTime),1.+cos(iTime));

