// -5 thanks to shadertoyjiang ðŸ¦”
void mainImage( out vec4 o, vec2 u )
{
    vec2 f = iResolution.xy;

    o = vec4(tanh(dot(u = 1.2*(u+u-f)/f.y, u)), 1.4, .9+.5*u.y, 1);

    for (float i; i < 1.; i += .05)      
        u *= mat2(cos(.1* cos(atan(u.y,u.x)*23.+iTime) 
                 - vec4(0,11,33,8))) / (1.15+pow(dot(u,u), 9.)),

        o = mix(o = min( o/(1.+i/40.), length(cos(u/i)/i) ),  
                abs(.045-o), 
                cos(u.x*23.));

    o = 1.-.6*o*o;
}
