void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;

    // Octopus body and arms
    float angle = atan(uv.y, uv.x);
    float radius = length(uv);
    float armPattern = sin(10.0 * angle + time * 5.0 - radius * 15.0);

    // Create dots for tentacles (suckers)
    float dotPattern = smoothstep(0.02, 0.03, cos(radius * 50.0 + armPattern * 5.0));

    // Color shifting based on angle and sound input
    float sound = texture(iChannel0, vec2(angle * 0.1, 0.5)).r;
    vec3 color = vec3(0.3 + 0.7 * sin(armPattern * 8.0 + time + sound * 5.0),
                      0.3 + 0.7 * sin(armPattern * 6.0 + time - sound * 4.0),
                      0.3 + 0.7 * sin(armPattern * 10.0 + time + sound * 6.0));

    // Apply the dot pattern to simulate tentacle suckers
    color *= dotPattern;

    // Apply glow effect
    float glow = smoothstep(0.15, 0.2, armPattern);
    color *= glow;

    // Create the octopus effect by blending colors and applying glow
    fragColor = vec4(color, 1.0);
}
