#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
vec4 noise (vec2 U) {
    vec2 f = floor(U);
    vec2 c = ceil(U);
    vec4 _00 = pow(hash44(vec4(f,f)),vec4(1,3,3,3));
    vec4 _01 = pow(hash44(vec4(f.x,c.y,f.x,c.y)),vec4(1,3,3,3));
    vec4 _10 = pow(hash44(vec4(c.x,f.y,c.x,f.y)),vec4(1,3,3,3));
    vec4 _11 = pow(hash44(vec4(c,c)),vec4(1,3,3,3));
    vec4 _0 = mix(_00,_01,fract(U.y));
    vec4 _1 = mix(_10,_11,fract(U.y));
    return mix(_0,_1,fract(U.x));
}
