void mainImage( out vec4 outputColor, in vec2 position)
{
    vec2 z = (position - .5 * iResolution.xy) / (.5*iResolution.y);
    float pw = 2./iResolution.y;

    float theta = iTime/60.0, c = cos(theta), s = sin(theta);
    float theta2 = iTime/111.0, c2 = cos(theta2), s2 = sin(theta2);
    mat3 spin = mat3(
        vec3(1, 0, 0),
        vec3(0, c, s),
        vec3(1, -s, c));
    mat3 spin2 = mat3(
        vec3( c2, s2,0),
        vec3(-s2, c2,0),
        vec3(0,0,0.1));

    vec3 color = vec3(0.1,0.3,0);

    if (length(z) < 1.0) {
        float scale = (1.0 + iTime/3.0);
        float scaled_pw = scale*pw;
        vec3 w = vec3(z.x, z.y, sqrt(1.0-dot(z,z))) * scale;
        vec3 w2 = vec3(w.x, w.y, -w.z);
        // Points from the two halves of the sphere.

        w = spin2 * spin * w;
        w2 = spin2 * spin * w2;
        #define F(p) ((.4 - length(p - round(p))) / scaled_pw)
        color = mix(color, vec3(.8, .5, .9),  smoothstep(0., 1., F(w)));
        color = mix(color, vec3(.9, .2, .6+color.b*.2),  smoothstep(0., 1., F(w2)));
    }
    outputColor = vec4(color, 1);
}
