#define PI 3.14159
#define R iResolution
#define dt iTime

const vec3
black=vec3(0.),
white=vec3(1.),
A=vec3(.5),
B=vec3(.5),
C=vec3(1.),
D=vec3(0,.2,.4);
vec3 palette(float t){return A+B*cos(2.*PI*(C*t+D));}

mat2 rot2D(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

float f(vec2 uv){
    uv*=1.5;
    float v,r,a,d,dr,t,th;
    th=.02;
    t=dt*.5;
    uv*=rot2D(sin(t)+.5*length(uv)*sin(t-1.));
    d=length(uv);
    a=atan(uv.y,uv.x);
    r=.5+.5*abs(cos(10.*a));
    dr=d-r;
    v=1.-smoothstep(0.,th,dr);
    dr=d-.2;
    v=v*smoothstep(0.,th,dr);
    return v;
}

void mainImage(out vec4 C, in vec2 I) {
    vec2 uv0 = (2.*I-R.xy)/R.y;
    vec3 col;
    vec2 uv=uv0;
    col=palette(sin(uv.x)*sin(uv.y)+dt*.2);
    uv=fract(uv*1.4)-.5;
    uv*=2.;
    vec3 c1,c2;
    if(abs(uv0.x)>.6||abs(uv0.y)>.6)c1=mix(black,col,f(uv));else c1=black;
    c2=mix(black,col,f(uv0*.8));
    col=max(c1,c2);
    C = vec4(col,1.0);
}
