#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(23.,53.,21.+cos(iTime)))*1.+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec4 O2=vec4(0);
    vec2 u =C;

     u /= iResolution.xy;         
    O2 *= 2.;
    O2.r++;
     u.y-=iTime*0.5;
     for (float t = .005*iTime, i; i++ < 7.; u = fract(u+u+.525)) 
        u.x < 0.55 ? u+=t, O2-- : O2=-O2,
        u.y < 0.55 ? u-=t, O2++ : O2--;


    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1.+cos(iTime),2.,1.+cos(iTime)),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        p=g*d+O2.xyz;

  p.z+=iTime*2.;
        a=30.;
        p.xz*=mat2(cos(iTime*0.055), sin(iTime*0.055), -sin(iTime*0.051),cos(iTime*0.055));
         p.xy*=mat2(cos(iTime*0.055), sin(iTime*0.055), -sin(iTime*0.051),cos(iTime*0.055));
        p=mod(p-a,a*2.)-a;
        s=3.;
        for(int i=0;i++<8;){
            p=.8-abs(p);

            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;
            p.y<p.x?p=p.zyx:p;

            s*=e=1.4+sin(iTime*.234)*.1;
            p=abs(p)*e-
                vec3(
                    5.+cos(iTime*.3+.5*cos(iTime*.3))*3.,
                    120,
                    8.+cos(iTime*.5)*5.
                 )+O2.xyz;
         }

         g+=e=length(p.yx)/s;
    }
}
