#define n_tau radians(360.0)

vec2 f_o_rotd(
    vec2 o, 
    float n_ang_nor
){
    float nrad = n_ang_nor*n_tau; 
    float nsin = sin(nrad);
    float ncos = cos(nrad);
    return o*mat2(ncos, -nsin, nsin, ncos);

}

float f_n_segment(vec2 o_trn_nor_pix) {
    vec2 o_scl_screen = iResolution.xy;
    o_trn_nor_pix = abs(o_trn_nor_pix);
    float n1 = o_trn_nor_pix.x;
    float nrad = -(1./8.)*n_tau; 
    float nsin = sin(nrad);
    float ncos = cos(nrad);
    float nh = 0.25;
    vec2 o2 = f_o_rotd(o_trn_nor_pix+vec2(0., -nh),-(1./8.));
    float n2 = o2.x; 
    return max(n1,n2);
}

float f_n_sevensegmentdisplay(
    vec2 o_trn_nor, 
    float n_num
){
    o_trn_nor *= 3.;
     float[] a = float[] (
         f_n_segment(f_o_rotd(o_trn_nor, 0.25)),
         f_n_segment(f_o_rotd(o_trn_nor+vec2(-.5,0.5), 0.0)), 
         f_n_segment(f_o_rotd(o_trn_nor+vec2(.0, 1.), 0.25)),
         f_n_segment(f_o_rotd(o_trn_nor+vec2(.5,0.5), 0.0)),
         f_n_segment(f_o_rotd(o_trn_nor+vec2(.5,-0.5), 0.0)),
         f_n_segment(f_o_rotd(o_trn_nor+vec2(.0, -1.), 0.25)),
         f_n_segment(f_o_rotd(o_trn_nor+vec2(-.5, -.5), 0.0))
     );
     float[] a_n = float[] (
         //0
         min(min(min(min(min(a[1],a[2]),a[3]),a[4]),a[5]),a[6]),
         //1 
         min(a[6], a[1]),
         //2 
         min(min(min(min(a[5],a[6]),a[0]),a[3]),a[2]),
         //3
         min(min(min(min(a[0],a[1]),a[2]),a[5]),a[6]),
         //4
         min(min(min(a[0],a[1]),a[4]),a[6]),
         //5
         min(min(min(min(a[0],a[1]),a[2]),a[4]),a[5]),
         //6
         min(min(min(min(min(a[0],a[1]),a[2]),a[3]),a[4]),a[5]),
         //7 
         min(min(a[1],a[5]),a[6]),
         //8
         min(min(min(min(min(min(a[0],a[1]),a[2]),a[3]),a[4]),a[5]),a[6]),
         //9
         min(min(min(min(min(a[0],a[1]),a[2]),a[4]),a[5]),a[6]), 
         //A
         min(min(min(min(min(a[0],a[1]),a[3]),a[4]),a[5]),a[6])

     );

    return a_n[int(n_num)];
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy);
    uv.x -= uv.y*0.03;
    float n_numbers = 5.;
    float n_x_to_y = (iResolution.x/n_numbers) / iResolution.y;
    uv.x *= n_numbers;
    float n_idx = floor(uv.x);
    vec2 o = fract(uv)-.5;
    //o = abs(o);
    float num = iTime;//2182.;
    float nn = mod(floor(num/pow(10.,(3.-n_idx))),10.);
    o *= vec2(n_x_to_y, 1.);
    o *= 2.;
    float n = f_n_sevensegmentdisplay(o, nn);
    //float nglow = 1.-pow(abs(n-.1), 1./3.);
    float naa = 1./iResolution.x;
    float nw = 0.15;
    float nss = smoothstep(nw, nw-naa*60., n);
    float nb = 0.14;
    float nglow = 1.-(-nb * (1./(nb+abs(n-nw)))+1.);
    vec3 o_red = vec3(
            0.8, 
            0.12, 
            0.12
        );
    fragColor = vec4(
        nss*o_red*8.+(nglow)*o_red,
        1.
    );
}
