void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 omouserule = texelFetch(iChannel0, ivec2(1, 1), 0);
    vec4 omouserule2 = texelFetch(iChannel0, ivec2(2, 1), 0);

    vec4 o = texture(iChannel0, uv);
    uv.x *= 2.;
    float n_rule = omouserule.y;
    if(uv.x > 1.){
      n_rule = omouserule2.y;
    }
    uv = fract(uv);
    uv.x *= 3.;
    uv.y *= 3.;
    uv.y = clamp(uv.y, 1., 2.);
    float n_idx_dig = floor(uv.x);

    float n_dig = floor(mod(n_rule / pow(10., 2.-n_idx_dig), 10.));

    vec4 ofont = texture(iChannel1, (fract(uv)+vec2(n_dig, 12))/16.);


    // Output to screen
    fragColor = o*vec4(1.-ofont.x)+min(ofont.x,min(ofont.y, ofont.z));
}

vec2 hash22(vec2 p)
{

// Hash without Sine
// MIT License...
