SOFTWARE.*/

	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 o_trn = fragCoord.xy / iResolution.xy;
   vec2 o_trn_mou = iMouse.xy / iResolution.xy;
   vec4 o = vec4(0.);
   //o = vec4(sin(o_trn.x+iTime));
   ivec2 ofc = ivec2(fragCoord.xy);
   vec4 omouserule = texelFetch(iChannel0, ivec2(1, 1), 0);
   vec4 omouserule2 = texelFetch(iChannel0, ivec2(2, 1), 0);

   int b_mouse_down_last = int(omouserule.x);
   int b_mouse_down = int(sign(iMouse.z)>0.);
   float n_rule1 = omouserule.y;
   float n_rule2 = omouserule2.y;
   float[] a_n_rule = float[] (omouserule.y, omouserule2.y);
   if(b_mouse_down_last ==  0 && b_mouse_down == 1){
       vec2 om2 = o_trn_mou*vec2(2., 1.);
       int n_idx = int(om2.x);

       float ns = sign(fract(om2.x)-.5);
       float n_rule = a_n_rule[n_idx];
       a_n_rule[n_idx] = n_rule + 1. * ns;
       if(n_rule < 0.){a_n_rule[n_idx] = 255.;}
       if(n_rule > 255.){a_n_rule[n_idx] = 0.;}

   }
   n_rule1 = a_n_rule[0];
   n_rule2 = a_n_rule[1];
   if(iFrame == 0){
       n_rule1 = 118.;
       n_rule2 = 82.;
   }

   if(fragCoord.y > 1.){
       o = texelFetch(iChannel0, ivec2(fragCoord.xy)-ivec2(0, 1), 0);
   }else{

       vec4 ocm1 = texelFetch(iChannel0, ivec2(fragCoord.xy)+ivec2(-1, 0), 0);
       vec4 oc1 = texelFetch(iChannel0, ivec2(fragCoord.xy)+ivec2(0, 0), 0);
       vec4 ocp1 = texelFetch(iChannel0, ivec2(fragCoord.xy)+ivec2(1, 0), 0);


       if(b_mouse_down_last ==  0 && b_mouse_down == 1){
           ocm1 = vec4(hash22(fragCoord.xy+vec2(-1., 0.)).x);
           oc1 = vec4(hash22(fragCoord.xy).x);
           ocp1 = vec4(hash22(fragCoord.xy+vec2(1., 0.)).x);
       }

       int n_idx = int(ocm1.x > 0.5) << 2 | int(oc1.x > 0.5) << 1 | int(ocp1.x > 0.5) << 0;
       int n_rule = int(omouserule.y);//int(fract(iTime*.1)*255.);
       int n_rule2 = int(omouserule2.y);//int(fract(iTime*.1)*255.);
       int n_new_state2 = n_rule2 & (1 << n_idx);
       n_rule = n_rule;
       if(o_trn.x > 1./4.){
           if((float(n_idx)/(pow(2., 3.))) > .5){
               n_rule = n_rule2; 
           }
       }
       if(o_trn.x > 2./4.){
            if(mod(float(n_idx), 2. ) == 0.){
               n_rule = n_rule2; 
           }
       }
       if(o_trn.x > 3./4.){
           n_rule = n_rule2;
       }


       int n_new_state = n_rule & (1 << n_idx);
       float n = float(n_new_state);

       o = vec4(n);

   }
   if(iFrame == 0){
    o = vec4(hash22(fragCoord.xy).x);
   }
   if(ofc.x == 1 && ofc.y == 1){

       o = vec4(
           b_mouse_down, 
           n_rule1, // n_rule
           0., 
           0.
       );
   }
   if(ofc.x == 2 && ofc.y == 1){

       o = vec4(
           b_mouse_down, 
           n_rule2, // n_rule
           0., 
           0.
       );
   }

   fragColor = o;
}
