#define S(x) 3. * x * x - 2. * x * x * x
#define T iTime * .2

float N21(vec2 p) {
    return fract(sin(p.x*13.21+p.y*6574.)*5647.);
}

float ValueNoise(vec2 st)
{
    vec2 gv = fract(st);
    vec2 id = floor(st);
    float bl = N21(id);
    float br = N21(id + vec2(1., .0));
    float tl = N21(id + vec2(.0, 1.));
    float tr = N21(id + vec2(1., 1.));
    vec2 uv = S(gv);
    float b = mix(bl, br, uv.x);
    float t = mix(tl, tr, uv.x);
    float n = mix(b, t, uv.y);
    return n;
}

float FBM(vec2 uv, float layers)
{
    float freq = 1.;
    float am   = 1.;
    float n    = .0;
    float divide = .0;
    for(float i = 0.; i < layers; i++)
    {
        n += ValueNoise(uv * freq) * am;
        divide += am;
        freq *= 2.;
        am   *=  .5;
    }
    return n / divide;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.y;
    uv += vec2(T);
    vec3 col = vec3(0.);
    float n = FBM(uv * 5., 5.0);
    col = vec3(n);
    fragColor = vec4(col,1.0);
}
