// According to "you can make something passive (like a scene of a movie or A BOOK)"
// I tried to implement a gamebook adventure.
// But I allways got the next error:
// Unknown error: 0(221) : error C5041: cannot locate suitable resource to 
// bind variable "[variable]". Possibly large array.
// OR Unknown error: error X4505: Sum of temp registers and indexable
// temp registers exceeds limit of 4096
//
// SORRY FOR MY ENGLISH. It is not my native language.
// This is not a good code, I just tired and don't have time to rewrite all in right way.


#define PI 3.1415927
#define RADIUS 0.1
#define CURL_LINE vec2(0.995, 0.0998)

//============================================================
// BEGIN UTIL
//============================================================

#define texel_size iChannelResolution[0].xy

vec4 load_vec4(int var_id) {
  float id = float(var_id);
  vec2 var_coord = (vec2(
          mod(id, iChannelResolution[3].y),
          floor(id / iChannelResolution[3].y)
  		) + 0.5) / texel_size;
  return texture(iChannel2, var_coord);
}

float hash1(uint n) {
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return 1.0 - float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}
//============================================================
// END UTIL
//============================================================

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xx;
    vec2 ur = vec2(1.0, iResolution.y/iResolution.x);
    vec2 mouse = iMouse.xy/iResolution.xx;

    vec4 val = load_vec4(0);
    float time = val.x,
        mouse_z = val.y,
        anim_state = val.z,
        anim_pos = val.w;

    float cosan = dot(uv, CURL_LINE);
    vec2 end = abs((ur - uv) / CURL_LINE);
    float maxt = cosan + min(end.x, end.y);
    float normal, curl_cosan;

    if (cosan < anim_pos - RADIUS) {
        normal = 1.0;
        curl_cosan = cosan;
    } else if (cosan > anim_pos + RADIUS) {
        curl_cosan = -1.;
    } else {
        float a = asin((cosan - anim_pos) / RADIUS);
        float ca = -a + PI;

        if (!false && anim_pos + ca * RADIUS < maxt) {
            normal = cos(ca);
            curl_cosan = anim_pos + ca * RADIUS;
        } else  if (cosan < anim_pos) {
            normal = 1.0;
            curl_cosan = cosan;
        } else if (anim_pos + a * RADIUS < maxt) {
            normal = cos(a);
            curl_cosan = anim_pos + a * RADIUS;
        } else {
        	curl_cosan = -1.;
        }
    }

    if (abs(maxt - curl_cosan) < 0.005) {
        fragColor *= 0.;
        return;
    } else if (curl_cosan > 0.) {
        vec2 tuv = uv + CURL_LINE * (curl_cosan - cosan);

        float shadow = 1.0 - smoothstep (0.0, RADIUS * 2.0, -(cosan - anim_pos));
   	    shadow *= (smoothstep(-RADIUS, RADIUS, (maxt - (curl_cosan + 1.5 * PI * RADIUS + RADIUS))));
        fragColor = texture(iChannel0, tuv / ur);
        uvec2 uuv = uvec2(tuv / ur * iResolution.xy);
        float noise = hash1(uuv.x + 1920U * uuv.y + (1920U*1080U)) / 15.;
        fragColor = normal > 0.0 ?
            	(fragColor - noise) * normal * (1. - shadow): 
        		-normal * (fragColor * 0.05 + 0.95 - noise);
    } else {
        float shadow = smoothstep(0.0, RADIUS * 2.0, (cosan - anim_pos));
        uvec2 uuv = uvec2(uv / ur * iResolution.xy);
        float noise = hash1(uuv.x + 1920U * uuv.y + (1920U*1080U)) / 15.;
        fragColor = (texture(iChannel1, uv / ur) -  noise) * shadow;
    }
}

