//#define CELL_SCALE
#define CELLS vec2(31, 16)
#define CELLS2 vec2(16, 16)

vec2 uv_to_cell_uv(vec2 uv) {
    vec2 nuv = floor(uv * CELLS);
    nuv.y = 15. - nuv.y;
    return nuv;
}

vec4 g(vec2 uv, ivec2 cell_uv, uint char_id) {
    vec2 cuv = vec2(cell_uv);
    vec2 char_offset = vec2(char_id % 16U, 16U - char_id / 16U) / 16.;
    return texture(iChannel1, (fract(uv * CELLS) - vec2(-0.5, 1)) / CELLS + char_offset).xxxx;
}
