void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 mouse = iMouse.xy / iResolution.xy;
    vec2 uv = fragCoord.xy / iResolution.y - vec2(0.25, 0.);
    uvec2 uuv = uvec2(fragCoord);

    fragColor *= 0.;

    ivec2 cell_uv = ivec2(uv_to_cell_uv(uv));

    vec4 text_state_data = load_vec4(1);
    if (cell_uv == clamp(cell_uv, ivec2(0), ivec2(CELLS))) {
        vec4 anim_data = load_vec4(0);

    uint page = uint(text_state_data.x * 5.);
    if (page == 0U)
            fragColor += g(uv, cell_uv, text_0[cell_uv.x + cell_uv.y * 32]);
        if (page == 1U)
            fragColor += g(uv, cell_uv, text_1[cell_uv.x + cell_uv.y * 32]);
        if (page == 2U)
            fragColor += g(uv, cell_uv, text_2[cell_uv.x + cell_uv.y * 32]);
        if (page == 3U)
            fragColor += g(uv, cell_uv, text_3[cell_uv.x + cell_uv.y * 32]);
        if (page == 4U)
            fragColor += g(uv, cell_uv, text_4[cell_uv.x + cell_uv.y * 32]);
        if (page == 5U)
            fragColor += g(uv, cell_uv, text_5[cell_uv.x + cell_uv.y * 32]);
        if (page == 6U)
            fragColor += g(uv, cell_uv, text_6[cell_uv.x + cell_uv.y * 32]);
        if (page == 7U)
            fragColor += g(uv, cell_uv, text_7[cell_uv.x + cell_uv.y * 32]);
    }

    fragColor = clamp(1. - fragColor, .2, .9);

    uint curr_page = uint(text_state_data.x * 5.);

    if (curr_page > 0U) {
        uint ans_line = uint(text_state_data.z * 5.);
        if (uint(uv.y * 16.) == ans_line) {
            fragColor.z = 0.;
        }
    }
}

