vec4 load_vec4(int var_id) {
  float id = float(var_id);
  vec2 var_coord = (vec2(
          mod(id, iChannelResolution[0].y),
          floor(id / iChannelResolution[0].y)
          ) + 0.5) / texel_size;
  return texture(iChannel0, var_coord);
}

