    uvec2 uuv = uvec2(fragCoord);

    fragColor *= 0.;

    ivec2 cell_uv = ivec2(uv_to_cell_uv(uv));

    vec4 text_state_data = load_vec4(1);
    if (cell_uv == clamp(cell_uv, ivec2(0), ivec2(CELLS))) {
        vec4 anim_data = load_vec4(0);

    uint page = uint(text_state_data.y * 5.);
    if (page == 0U)
            fragColor += g(uv, cell_uv, text_0[cell_uv.x + cell_uv.y * 32]);
        if (page == 1U)
            fragColor += g(uv, cell_uv, text_1[cell_uv.x + cell_uv.y * 32]);
        if (page == 2U)
            fragColor += g(uv, cell_uv, text_2[cell_uv.x + cell_uv.y * 32]);
        if (page == 3U)
            fragColor += g(uv, cell_uv, text_3[cell_uv.x + cell_uv.y * 32]);
        if (page == 4U)
            fragColor += g(uv, cell_uv, text_4[cell_uv.x + cell_uv.y * 32]);
        if (page == 5U)
            fragColor += g(uv, cell_uv, text_5[cell_uv.x + cell_uv.y * 32]);
        if (page == 6U)
            fragColor += g(uv, cell_uv, text_6[cell_uv.x + cell_uv.y * 32]);
        if (page == 7U)
            fragColor += g(uv, cell_uv, text_7[cell_uv.x + cell_uv.y * 32]);
    }

    fragColor = clamp(1. - fragColor, .2, .9);
}

const float KEY_UP    = 38.5/256.0;
const float KEY_W     = 87.5/256.0;

const float KEY_DOWN  = 40.5/256.0;
const float KEY_S     = 83.5/256.0;

//============================================================
// BEGIN UTIL
//============================================================

vec4 load_vec4(int var_id) {
  float id = float(var_id);
  vec2 var_coord = (vec2(
          mod(id, iResolution.y),
          floor(id / iResolution.y)
  		) + 0.5) / iResolution.xy;
  return texture(iChannel0, var_coord);
}

void save_vec4(int var_id, vec4 val, inout vec4 fragColor, vec2 fragCoord) {
  float id = float(var_id);
  vec2 d = abs(fragCoord - vec2(
          mod(id, iResolution.y),
          floor(id / iResolution.y)
  		) - 0.5);
  if (max(d.x, d.y) < 0.5)
      fragColor = val;
}

//============================================================
// END UTIL
//============================================================

vec4 pages_data[] = vec4[](vec4(0));

#define SPEED 0.3

uvec4 story_line[] = uvec4[](uvec4(1, 1, 0, 0),
                          uvec4(2, 2, 3, 0),
                          uvec4(1, 0, 0, 0),
                          uvec4(2, 4, 5, 0),
                          uvec4(1, 0, 6, 0),
                          uvec4(2, 7, 6, 0),
                          uvec4(1, 0, 0, 0),
                          uvec4(1, 0, 0, 0));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec4 anim_data;
    vec4 text_state_data;
    if (iFrame < 10) {
        anim_data = vec4(iTime, iMouse.z, 0., 1.);
        text_state_data = vec4(0., 0., 17. / 5., 0.);
    } else {
        anim_data = load_vec4(0);
        text_state_data = load_vec4(1);

        float time = anim_data.x,
            mouse_z = anim_data.y,
            anim_state = anim_data.z,
            anim_pos = anim_data.w;
        uint next_page = uint(text_state_data.y * 5.),
            ans_line = uint(text_state_data.z * 5.),
            curr_page = uint(text_state_data.x * 5.);
        uvec4 chapter_data = story_line[curr_page];

        // hack
        if (anim_pos < -0.2 && abs(anim_state - 0.) < 0.01) {
            anim_pos = 1.;
        }

        if (iMouse.z > 0.) {
            if (curr_page == 0U) {
                mouse_z = iMouse.z;
                if (abs(anim_pos - mouse.x) < iTimeDelta * SPEED * 2.) {
                    anim_pos = mouse.x;
                } else {
                    anim_pos -= sign(anim_pos - mouse.x) * iTimeDelta * SPEED * 2.;
                }
                next_page = chapter_data[1];
            } else {
                int id = int(floor(mouse.y * 16.));

                if (chapter_data[0] == 1u && id == 1) {
                    next_page = chapter_data[1];
                    ans_line = uint(id);
                    anim_state = 2.;
                } else if(int(chapter_data[0]) - id > 0) {
                    anim_state = 2.;
                    ans_line = uint(id);
                    next_page = chapter_data[int(chapter_data[0]) - id];
                }
            }
        } else if (iMouse.z <= 0. && mouse_z > 0.) {
            mouse_z = iMouse.z;
            if (mouse.x > 0.5) {
            	anim_state = 1.;
            } else {
                anim_state = 2.;
            }
        } else if (iMouse.z <= 0. && mouse_z <= 0. && anim_state > 0.) {
            if (anim_pos < -0.2) {
                if (abs(anim_state - 2.) < 0.01) {
                    curr_page = next_page;
                    ans_line = uint(17.);
                }
                anim_state = 0.;
                time = iTime;
            } else {
                if (abs(anim_state - 1.) < 0.01) {
                    anim_pos += iTimeDelta * SPEED;
                } else {
                    anim_pos -= iTimeDelta * SPEED * 2.;
                }
            }
        }
        anim_data = vec4(time, mouse_z, anim_state, anim_pos);
        text_state_data = vec4(float(curr_page) / 5., float(next_page) / 5., float(ans_line) / 5., 0);
    }

    save_vec4(0, anim_data, fragColor, fragCoord);
    save_vec4(1, text_state_data, fragColor, fragCoord);

}
