vec2 ballTexture_Adress = vec2(0.0, 0.0);
vec2 paddleTexture_Adress = vec2(40.0, 20.0);
vec2 blockTexture_Adress = vec2(0.0, 35.0);
vec2 blockTexture2_Adress = vec2(0.0, 50.0);
vec2 blockTexture3_Adress = vec2(0.0, 65.0);
vec2 bgTexture_Adress = vec2(0.0, 80.0);

float blockWidth = 50.0;
float blockHeight = 15.0;
vec2 block_Adress = vec2(3.0, 50.0);
vec4[50] block_Data;

vec2 ball_Adress = vec2(1.0, 0);
vec4 ballData;

vec2 trail_Adress = vec2(8.0, 50);
vec4[90] trail_Data;

float ballRadius = 10.0;
float paddleWidth = 80.0;
float paddleHeight = 15.0;



void DrawBallSprite(vec2 screenCoord, vec2 pos, inout vec4 pixel)
{
  if (screenCoord.x >=pos.x && screenCoord.x <=pos.x+20.0 && screenCoord.y >=pos.y && screenCoord.y <=pos.y+20.0)
  {
    vec2 dist = (screenCoord-pos)/iResolution.xy;
    vec2 loc = ballTexture_Adress/iResolution.xy;    
    vec4 c =texture(iChannel3, loc+dist);

    pixel=mix(pixel, c, c.w);
  }
}


void DrawTrailBallSprite(vec2 screenCoord, vec2 pos, vec4 tint, inout vec4 pixel)
{
  if (screenCoord.x >=pos.x && screenCoord.x <=pos.x+20.0 && screenCoord.y >=pos.y && screenCoord.y <=pos.y+20.0)
  {
    vec2 dist = (screenCoord-pos)/iResolution.xy;
    vec2 loc = ballTexture_Adress/iResolution.xy;    
    vec4 c =texture(iChannel3, loc+dist);

    pixel=mix(pixel, c*tint, c.w*tint.w);
  }
}

void DrawPaddleSprite(vec2 screenCoord, vec2 pos, inout vec4 pixel)
{
  if (screenCoord.x >=pos.x && screenCoord.x <=pos.x+80.0 && screenCoord.y >=pos.y && screenCoord.y <=pos.y+15.0)
  {
    vec2 dist = (screenCoord-pos)/iResolution.xy;
    vec2 loc = paddleTexture_Adress/iResolution.xy;    
    vec4 c =texture(iChannel0, loc+dist);

    pixel=mix(pixel, c, c.w);
  }
}

void DrawBlockSprite(vec2 screenCoord, float health, vec2 pos, vec4 tint, inout vec4 pixel)
{
  if (screenCoord.x >=pos.x && screenCoord.x <=pos.x+50.0 && screenCoord.y >=pos.y && screenCoord.y <=pos.y+15.0)
  {
    vec2 dist = (screenCoord-pos)/iResolution.xy;

    vec2 loc;
    if (health>2.0)
    {
      loc = blockTexture_Adress/iResolution.xy;
    } else  if (health>1.0)
    {
      loc = blockTexture2_Adress/iResolution.xy;
    } else
    {
      loc = blockTexture3_Adress/iResolution.xy;
    }
    pixel=texture(iChannel3, loc+dist)*tint;
  }
}




vec4 read(in sampler2D buffer, in vec2 memPos, vec2 resolution)
{
  return texture(buffer, (memPos+0.5)/resolution, -100.);
}


float modulo(float a, float b)
{
  return a - (b * floor(a/b));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   

  float frame = float(iTime)*60.0;

  vec2 uv = (fragCoord.xy / iResolution.xy);
  vec4 music = texture(iChannel2, uv*0.1);  
  float musicVol = length(music);

  vec2 screenCoord = vec2(fragCoord.x, iResolution.y-fragCoord.y);

  vec2 texCoord = (bgTexture_Adress+vec2(modulo(screenCoord.x*0.5-float(iTime)*15.0, 23.0), modulo(screenCoord.y*(uv.y*musicVol), 23.0)))/ iResolution.xy;
  vec4 color=texture(iChannel3, texCoord)* (1.5-(distance(vec2(0.5), uv)));

  for (int i=0; i<50; i++)
  {       
    trail_Data[i] =  read(iChannel0, vec2(trail_Adress.x, trail_Adress.y-float(i)), iChannelResolution[0].xy);
    float trailMix=(trail_Data[i].z*0.0111);

    DrawTrailBallSprite(screenCoord, trail_Data[i].xy-vec2(ballRadius), vec4(1.0, 1.0, 1.0, (trailMix)*.4), color);
  }

  // ball
  ballData = read(iChannel0, ball_Adress, iChannelResolution[0].xy); 

  color *=0.6+(3.0/max(1.0, (distance(ballData.xy, screenCoord)*0.07)));

  DrawBallSprite(screenCoord, ballData.xy-vec2(ballRadius), color);

  // paddle
  vec2 lowerPaddlePos = vec2(max(0.0, min(iResolution.x-paddleWidth, iMouse.x)), iResolution.y-20.0-paddleHeight);     
  vec2 upperPaddlePos = vec2(max(0.0, min(iResolution.x-paddleWidth, iMouse.x)), 20.0);

  float paddleRight=  lowerPaddlePos.x +paddleWidth;
  float paddleBottom=  lowerPaddlePos.y +paddleHeight;

  DrawPaddleSprite(screenCoord, lowerPaddlePos, color);
  DrawPaddleSprite(screenCoord, upperPaddlePos, color);

  float rumbleAmount = -40.0+ (40.0*musicVol);

  // blocks
  for (int i=0; i<50; i++)
  {
    block_Data[i] =  read(iChannel0, vec2(block_Adress.x, block_Adress.y-float(i)), iChannelResolution[0].xy);
    if (block_Data[i].w>=1.0)
    {         
      block_Data[i].y +=rumbleAmount;


      vec4 tint = vec4(0.5+(0.25*block_Data[i].w)+(5.0*block_Data[i].z));

      DrawBlockSprite(screenCoord, block_Data[i].w, block_Data[i].xy, tint, color);
    }
  }

  fragColor = color;
}
