#define PI 3.14159 
#define TAU (2. * PI)
#define HPI (PI / 2.)

float noise(vec2 p) // NO NOISE
{
    return fract(dot(sin(p.x * 123.12)*142.,cos(p.y *34.95)*165.47));
}

float revision(vec2 uv, float s)
{

    float dist = length(uv);
    float ang = atan(uv.y,uv.x); // NO ANGLE

    float cellSize = 7.5;
    float ringId = floor(dist * cellSize + .065);

    ang += iTime * (mod(ringId + s, 2.)* 2. - 1.)* .1  * (ringId + 1.);
    ang = mod(ang, TAU);

    float f = sin(dist * 44.) * .5 + .5;

	float thick = noise(vec2(floor(ang * 3.) / 3. + 100. + s * 30., ringId));    
    thick = floor(thick * 3.) / 3. + .15;
    f = smoothstep(thick - .005,.005 + thick,f); // NO CIRCLE
    f *= step(dist,.4);
    f *= step(.03,dist);
    return f;
}

void lineGlitch(inout vec2 uv, float force)
{

	uv.y += force * uv.x * .05;
    uv.x += force * sin(noise(vec2(force * 20.))*TAU) * noise(vec2(floor(uv.y * 30.) ) ) * .25;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // NO COORD

    float len = 6.;
    float time = mod(iTime,len);
    float seed = floor(iTime / len);
    float glitch = pow(1. - time * 1. / len, 10.);


    uv -= .5; // NO CENTER
	uv.x *= iResolution.x / iResolution.y;
    uv *= .8; // NO ZOOM


    lineGlitch(uv, glitch);

    float f = revision(uv, seed);

    vec3 col =vec3(f );

    fragColor = vec4(col,1.0); // JUST SHADE
}
