/*
Triangle wave implemented as integral of squarewave
See https://www.shadertoy.com/view/MlcSWS for some explanation
*/

const float eps=.00034526;

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float border=1.3/iResolution.y;

	vec2 uv=fragCoord.xy/iResolution.xy;
	uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    float t0=mod(iTime,freq_modulus);

    float audio_freq=get_audio_freq(t0);

    n=int((iSampleRate*.5+1.)/(2.*audio_freq)+.5);

	float freq=.0002*audio_freq;

	float zoom=5.;

    vec2 uv1=uv;
    uv1.y-=.25*sign(uv.y);

    uv1*=zoom;

	float dis1=abs(integral_triangle(uv1.x,freq)-uv1.y);
	dis1/=zoom*length(vec2((integral_triangle(uv1.x,freq)-integral_triangle((uv1.x+eps),freq))/eps,1.));

	float dis2=abs(additive_triangle(uv1.x,freq)-uv1.y);
	dis2/=zoom*length(vec2((additive_triangle(uv1.x,freq)-additive_triangle((uv1.x+eps),freq))/eps,1.));

	float dis3=abs(simple_triangle(uv1.x,freq)-uv1.y);
	dis3/=zoom*length(vec2((simple_triangle(uv1.x,freq)-simple_triangle((uv1.x+eps),freq))/eps,1.));

	float thickness=zoom*.0001;

	vec3 bg_col=vec3(0);

	vec3 add_col=vec3(1);
	vec3 int_col=vec3(1,0,0);
	vec3 smp_col=vec3(0,1,0);

	vec3 color;

    if(uv.y>0.){
    	color=mix(smp_col,bg_col,smoothstep(0.,border,dis3-thickness));
		color=mix(add_col,color,smoothstep(0.,border,dis2-thickness));
		color=mix(int_col,color,smoothstep(0.,border,dis1-thickness));
    }
    else{
        float t1=mod(iTime,wave_modulus);

        int wave_index=get_wave_index(t1);

        if(wave_index==0){
        	color=mix(smp_col,bg_col,smoothstep(0.,border,dis3-thickness));
        }
        else if(wave_index==1){
			color=mix(add_col,bg_col,smoothstep(0.,border,dis2-thickness));
        }
        else{
			color=mix(int_col,bg_col,smoothstep(0.,border,dis1-thickness));
        }
    }

	fragColor=vec4(color,1);
}

const float pi=3.1416;
const int max_n=100;
int n;

const float freq_modulus=18.;
const float wave_modulus=6.;

float polynomial(vec4 a, float x){
	return (((x*x + a[0])*x*x + a[1])*x*x + a[2])*x*x+a[3];
}

float rational(vec4 a, vec4 b, float x){

	float numerator=polynomial(a,x);
	float denominator=polynomial(b,x);

	return numerator/denominator;
}

float Si(float x){

	float sgn=1.;

	if(x<0.){
		sgn=-1.;
		x=-x;
	}

	if(x<1.){
		vec4 a = vec4(-1./35280.,1./600.,-1./18.,1.)*3265920.;
		return sgn*polynomial(a,x)/3265920.*x;
	}
	else{

		vec4 a_f=vec4(38.027264,265.187033,335.67732,38.102495);
		vec4 b_f=vec4(40.021433,322.624911,570.236280,157.105423);

		float f=1./x*rational(a_f,b_f,x);

		vec4 a_g=vec4(42.242855,302.757865,352.018498,21.821899);
		vec4 b_g=vec4(48.196927,482.485984,1114.978885,449.690326);

		float g=1./(x*x)*rational(a_g,b_g,x);

		return sgn*(pi/2.-f*cos(x)-g*sin(x));
	}
}

float integral_triangle(float x, float freq){
	x*=freq;

	float a=(pi/2.)*float(n)*8.;

	float sgn=1.;
	float pot_offset=2.*(a*.25*Si(a*.25)+cos(a*.25))/a;
	float offset=0.;

	if(fract(x)>=0.25 && fract(x)<0.75){
		sgn=-1.;
		offset=pot_offset;
	}

	x=mod(x+.25,.5)-.25;

	return (1./(.5*(pot_offset-2./a)))*((offset+sgn*(a*x*Si(a*x)+cos(a*x))/a)-.5*(pot_offset));
}

float additive_triangle(float x, float freq){

	float sum=0.;
	float amp=0.;

	for(int i=0;i<max_n;i++){
		if(i<n){
			float k = 2.*float(i)+1.;
			amp+=(2./(k*k));
			sum+=2./(k*k)*cos(mod(2.*pi*k*freq*x,2.*pi));
		}
	}

	return -sum/amp;
}

float simple_triangle(float x, float freq){
	x*=freq;
	return -(abs(mod(x,1.)-.5)-.25)*4.;
}

float get_audio_freq(float t){
    if(t<6.){
        return 1046.5022612024;//C8
    }
    else if(t<12.){
        return 2637.0204553030;//E9
    }
    else{
        return 4186.0090448096;//C10
    }
}

int get_wave_index(float t){
    if(t<2.){
    	return 0;
    }
    else if(t<4.){
		return 1;
    }
    else{
		return 2;
    }
}

vec2 mainSound( in int samp,float time){

    float t0=mod(time,freq_modulus);
    float t1=mod(time,wave_modulus);

    float audio_freq=get_audio_freq(t0);
    n=int((iSampleRate*.5+1.)/(2.*audio_freq)+.5);

    int wave_index=get_wave_index(t1);

    if(wave_index==0){
    	return vec2(.2*simple_triangle(t0,audio_freq));
    }
    else if(wave_index==1){
    	return vec2(.2*additive_triangle(t0,audio_freq));
    }
    else{
    	return vec2(.2*integral_triangle(t0,audio_freq));
    }
}
