int julia(vec2 uv, vec2 c){
int i=0;
while (i<100 && length(uv)<2.){
uv = vec2(uv.x*uv.x - uv.y*uv.y, 2.*uv.x*uv.y)+c;
i += 1;}
return i;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

  float time = iTime;
  vec2 touch = iMouse.xy;

  vec2 uv =-2.+4.*fragCoord.xy / iResolution.xy;
  uv.y *= iResolution.y/iResolution.x;
  float t = .1* time;
  vec2 touc = -1. + 2.*touch.xy/iResolution.xy;
  touc.y *= -iResolution.y/iResolution.x;
  float r = clamp(length (touc),.5,1.5);
  float a= atan(touc.y,touc.x);

  //julia
  float j = float(julia(uv, r*vec2(cos(t+a),sin(t+a))))/(100.*.3);

  fragColor = vec4(j);
}
