// hexagonal version of https://shadertoy.com/view/lsyfDD
// variant of https://shadertoy.com/view/lsVBWw

// 2 possible strategies: 
// - offseting odd lines of grid, as here. issue: more complicates tests.
// - global slant of the grid. issue: negative domain top-left. modulo needed.

// --- read map T.y and color cycle timer T.x

void mainImage( out vec4 O, vec2 U ) {
    float d;
    U = Hcoords(U/PIX,d);                              // hexa cell center and distance
    O = T( U );                                        // cell content
  //O = ( .5+.5*sin( O.x*vec4(1,2,4,8)) ) * O.y;       // coloring scheme
    O = ( .5+.5*sin( O.x + vec4(0,23,21,0) ) ) * O.y;  // coloring scheme
    O = mix( vec4(.5), O, smoothstep(0., 3./PIX, d) ); // hexa cells border
}

// --- paint and propagate timer

float id = 1.;          // if you want to test mixing several tracks

void mainImage( out vec4 O, vec2 U )
{
    float d; 
    bool s = int(U.y) % 2 > 0;
    vec2 R = iResolution.xy/PIX,        
         M = iMouse.xy/PIX,
         P = floor(U) -.5*vec2(s,0); 
    M = Hcoords(M,d);                         // hexa cell under Mouse

    if (iFrame==0) {                          // init drawing
        if ( abs( length( P - R*vec2(.5,1.) ) -R.x*.5 ) < 1. ) O.y = 2.;
        if ( abs( length( P - R*vec2(.5,0 ) ) -R.x*.5 ) < 1. ) O.y = 1.;
    }
    else O = T(U);

    if ( U-.5 == floor(M) )                   // on mouse:
        O.x = float(iFrame),                  //   draw (or erase if SPACE)
        O.y = id-keyClick(32);                //   and pace timer
    else 
      if (O.y==id)                            // in painted area:
        for(int k =0; k<9; k++) 
        if ( s && k!=2 && k != 8 || !s && k != 0 && k != 6 ) {
            vec4 T = T(U+vec2(k%3-1,k/3-1));  //   propagate max neighbor time
                if (T.y==id) O.x = max(O.x,T.x);
         }
    // maybe compiles better if separating 2 loops s, !s ?
}

#define PIX 16. // pixel size (i.e. zoom )

#define SQR3S2 0.866

#define T(U) texelFetch( iChannel0, ivec2(U), 0 )

#define keyToggle(c) (texture(iChannel3,vec2((.5+float(c))/256.,0.75)).x > 0.)
#define keyClick(c)   texture(iChannel3,vec2((.5+float(c))/256.,0.25)).x

// --- return hexa cell center + dist to center

vec2 Hcoords( vec2 U, out float d ) {
    U.y /= SQR3S2;
    float s = sign(mod(U.y,2.)-1.); 
    U.x -= .5*fract(U.y*s); 

    vec2 F = fract(U);                       // triangular barycentric coordinates
    if (s<0.) F.x = 1.-F.x;
    bool u = F.x+F.y > 1.;       
    vec3 q = vec3(F, fract(1.-F.x-F.y));
    if (u) q = 1.-q;
    vec3   r = q - q.zxy;

	vec2 S;                                  // hexagonal center, d = dist to border
    if      ( r.x>0. && r.y<0. ) S = vec2(!u,u), d = min(r.x,-r.y);
    else if ( r.y>0. && r.z<0. ) S = vec2(u,!u), d = min(r.y,-r.z);
    else                         S = vec2(u)   , d = min(r.z,-r.x);
    if (s<0.) S.x = 1.-S.x;    

    return floor(U)+S;
}
