void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - .5;

   	float h = 1.;
    int layers = 80;
    float d = 1./float(layers);

    // perspective controls
    float s = .85;
    float ss = pow( 1./s, d );

//    while ( h > .0 && texture(iChannel0,uv*s+.5).a < h )
    for ( int i=0; i < layers; i++ )
    {
        if ( texture(iChannel0,uv*s+.5).a >= h )
            break;
        s *= ss;//mix(s,1.,ss);
        h -= d;
    }

	fragColor = texture(iChannel0,uv*s+.5);

    // some fakey lighting
    float l = fragColor.a - texture(iChannel0,uv*s+.5+vec2(-1,.5)/iResolution.x).a;
    l = smoothstep( -.003, .01, l );

    // shadows
    float shadow = fragColor.a - texture(iChannel0,uv*s+.5+vec2(-.04,.02)).a;
    shadow = smoothstep( -.1, .0, shadow );

    fragColor.rgb *= mix( vec3(.1,.2,.3), vec3(1.1,1,.9), min(l, shadow) );

    // gamma correct
    fragColor.rgb = pow( fragColor.rgb, vec3(1./2.2) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int speed = int(ceil(iResolution.y/400.));

    if ( fragCoord.y > iResolution.y-float(speed) )
    {
        // generate new data

        int offset = -speed*iFrame;
        vec2 pos = vec2(0,offset);

        vec2 uv = (fragCoord-pos)/iResolution.x - .5;

        float height = .0;
        vec2 ouv = uv*.3;
        float a = 1.;
        for ( int i=0; i<5; i++ )
        {
        	height += pow(1.-texture(iChannel1,ouv).x,4.) * a;
            ouv = 4.*(ouv*.8+.6*ouv.yx*vec2(1,-1));
            a /= 4.;
        }
//        height = pow(height,4.);

        vec3 rock = texture(iChannel2,uv*5.).rgb;
        vec3 col = rock*.7;

/*        float roadX = sin( uv.y )*.3 + sin( uv.y*4. )*.1;
        float roadDX = cos( uv.y )*.3 + cos( uv.y*4. )*4.*.1;
        float roadDF = abs((uv.x-roadX) * sqrt(1.-roadDX*roadDX));*/

        float forestheight = texture(iChannel1,uv*11.+vec2(-3,3)*uv.yx).x
            				+ texture(iChannel1,uv*29.+vec2(3,-3)*uv.yx).x;

        float rockmask = height;//(height*.7-.2)*smoothstep( .08, .5, roadDF ) + .5*smoothstep( .3, .5, roadDF );
        float forestheight2 = .04*forestheight + height*.9+.03*rock.z-.02;
        rockmask = max( .0, rockmask-forestheight2 );
        height = rockmask + forestheight2;
//        height = mix( .0, height, smoothstep( .05,.06, roadDF ) );

        float snow = smoothstep(.2,1.,height*(1.-rock.z));
        height += snow*.05;

        vec3 grasscol = vec3(.1,.5,.15)*mix(rock,vec3(.5),.2);
        grasscol *= mix( 1., forestheight, .5 );
        col = mix( col, grasscol, smoothstep(.02,.0,rockmask) );
        col = mix( col, vec3(1), smoothstep(.0,.2,snow) );
//        col = mix( col, rock*.03+.02, smoothstep( .06, .05, roadDF ) );
//        col = mix( col, vec3(.8), smoothstep( .004, .002, roadDF )*smoothstep(-.4,-.2,sin(uv.y*100.) ) );

        fragColor.rgb = col*height;
        fragColor.a = height;
    }
    else
    {
	    // scroll the buffer

        fragColor = texelFetch(iChannel0,ivec2(int(fragCoord.x),int(fragCoord.y)+speed),0);
    }
}
