// Eye test - Del 15/02/18

#define saturate(x) clamp(x, 0.0, 1.0)
float disc(vec2 uv, float size1, float size2)
{
    float _smooth = 0.1;
    float l = length(uv*2.0);
    float d = smoothstep(size1, size1 + _smooth, l);
    d *= smoothstep(size1+size2, size1 + size2 + _smooth, 1.0-l);
    return d;
}
vec2 distortUV(vec2 uv, float size)
{
    vec2 m = vec2(0.5, 0.5);
    vec2 d = uv - m;
    float r = sqrt(dot(d, d));
    float p = (2.0 * 3.141 / (2.0 * sqrt(dot(m, m)))) * (size);
    float b = sqrt(dot(m, m));
    uv = m + normalize(d) * tan(r * p) * b / tan(b * p);
    //uv = saturate(uv);
    return uv;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - 0.5* iResolution.xy ) / iResolution.y;
    vec2 uv1 = distortUV(uv+vec2(0.5,0.5),0.28);
    float _x = 0.5+sin(iTime*1.1)*0.5;
    float _y = 0.5+sin(iTime*1.61)*0.15;
    uv1 -= vec2(_x,_y);

    float _d = length(uv1);
    float innerd = 1.0-step(_d,0.1);
    vec3 background = vec3(1.0,1.0,1.0)*innerd;

    float v = disc(uv1,0.1,0.4);

    vec3 col = vec3(0.3,0.6,1.0)*1.0-_d;

    col = mix( background, col, v);


	fragColor = vec4(col,1.0);
}
