// smoothtri sun / flower test... - Del 29/04/18

#define	PI 3.14159
#define	TAU 6.28318

float circle(vec2 pq, float radius)
{
    float pix = 3. / iResolution.y;
    return smoothstep(pix, -pix, pq.y - radius);
}

float SmoothTri(float t, float val)
{
	return val - (val * abs(cos(t*PI)) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2. * fragCoord.xy - iResolution.xy ) / iResolution.y;

    float _d = length(uv);

    vec2 pq = vec2(atan(uv.x, uv.y) / TAU, _d);

	float h = pq.x - iTime * .075;
    float nseg = 10.0;
    float segheight = 0.35;

    float stime1 = 0.5+sin(iTime)*0.5;
    float stime2 = 0.5+sin(iTime*0.1)*0.5;

    segheight += stime1*0.25;

    float d = SmoothTri(h*nseg,segheight);
    if (iMouse.z > 0.5)
        d = segheight-d;							// inverted :)

	float c0 = circle(pq, .45+(stime2*0.1) + d);

    vec3 sky = vec3(0.3,0.3,0.75);
    vec3 sun = mix(vec3(1.0,1.0,0.8),vec3(0.85,0.85,0.25),_d);
    vec3 col = mix(sky,sun,c0);
	fragColor = vec4(col, 1.);
}
