#define PI  3.14159265359
#define FRAMES (8.)

vec2 rotate(in vec2 p, in float t)
{
    return vec2(p.x * cos(t) - p.y * sin(t), p.y * cos(t) + p.x * sin(t));
}

float df_box (in vec2 p) { return max(abs(p.x), abs(p.y)); }

float sharpen(in float d, in float w)
{
    float e = 1. / min(iResolution.y , iResolution.x);
    return 1. - smoothstep(-e, e, d - w);
}

float tex(vec2 uv, vec2 fc)
{   
    for (float i = 0.; i < FRAMES; ++i) { 
        if (mod(floor(fc.x), FRAMES) == i) {
			return 1.-sharpen(df_box((rotate(uv,i*((PI/2.) / FRAMES)))*vec2(.03)), 0.0175);
        }
    }            
    return 0.;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy / iResolution.xy * 2. - 1.)
        	* vec2(iResolution.x/iResolution.y, 1) * 1.1;

    float d = tex(uv, fragCoord) > 0. ? 1. : 0.;
    float c = d;
    if (iMouse.z > 0.5 && mod(fragCoord.x + floor(iTime*10.), FRAMES) > .5) c = 0.;    
    fragColor = vec4(vec3(c), 1);
}
