// golfed version of https://www.shadertoy.com/view/lslcWj without color scheme

void mainImage(out vec4 O, vec2 U) {
    float t = iDate.w*.5,  C=cos(t),S=sin(t), 
          s = 7.; // 6.25 + 2.75* sin(t*.86);
    mat2  R = mat2( C, -S, S, C );
    vec3  q = iResolution,
          D = vec3((U+U-q.xy)/q.y, -1),
          p=q-q; p.z = 1.8; // 1.6 + .4* sin(t*.62);

    for ( O+=1.-O; O.x > 0. && t > .001 ; O-=.015)
        q = s*p,
        q.xz *= R,
        q.yz *= R,
        t = max( length(q) - s,                  // Sphere
                 abs(dot(q=sin(q),D/D))/3.) / s, // EggCarton
        p += t*D;
}




