#define PI 3.14159265358979323846

#define W(x,k,c) A*sin(k*(X=x*2.-c*t))*exp(-X*X)

#define C( r, R, t) smoothstep(t, 0., abs(R-(r)) )

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float A=3., X, y, Y=0.,  T = iTime,  t=mod(T,5.);

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = uv - vec2(0.5);
    p.x *= iResolution.x / iResolution.y;

    float pr = length(p);
    float pa = atan(p.y, p.x);

    float radius;

    float o = 0.0;

	float wave_width = 0.01;

    for( float i = 1.0 ; i < 10.0 ; i += 1.0 )
    {
        radius = .1 + i*i/500. + 0.01 * ( W( (cos(pa * i / 5.) + PI/2.), i, sqrt(i) ) );

    	o += C(radius + (sin(T)+1.)/10., pr, 8./iResolution.y);
    }

    fragColor = mix( vec4(0.7, 0.85, 1. - 0.2*p.y, 0) * (1.0 - 0.1 * pr), o*.7 + p.xyyy * sin(T), o );
}

