{
    vec2 r = iResolution.xy, uv = -1. + 2. * U.xy / r.xy,  m = iMouse.xy / r.xy - .5;
    uv.x *= r.x / r.y;    
    float t = iTime, a =atan(m.y, m.x) +  floor(iTime*.2)*20.0;
    uv.x+=2.0;
    uv = mat2(cos(a),-sin(a),sin(a),cos(a)) * uv;
    uv.y+=iTime*.00000037;
  float lines = random(uv*9999.123);
    lines = floor(lines * 30.0) / 30.0;
    O = vec4(lines);
}

vec2 mainSound( in int samp, float time )
{
    float m = step(sin(time*20.5),0.0);
    vec2 st = vec2(cos(time*120.212*m),sin(-time*0.73));
    st.xy *= fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123)+1.7;
    return st;
}
