/********* ABOUT **********/
/* Infinite hattifnatters surrounding a lightning rod.
/* Lightning strikes every 10ish secs.
/* Music transcribed from a Moomin episode.
/* Click and drag to control position (vertical) and rotation (horizontal)
/* Toggle settings on/off) to see different (mostly debug) views.

pres outline:
- inspiration (screenshot of episode, what are they)
- no triangles (instead of e.g. modeling in Blender, this is all code)
- just distance
- for each pixel, this code (called shader) is run
- 2d march illustration (explain lil better here) (emphasize that center is camera)
- show 2d ver of scene (helps explanation)
- back to 3d w/o hats (plane)
- grid
- hash
- buildup model
- lightning + extra
- aaaaaand the music you've heard comes from another shader (it was code all along)
- ask me 4 questions later :)
/**************************/

/******** SETTINGS ********/
//#define IS_2D
//#define SINGLE

//#define SHOW_DEPTH
//#define SHOW_QUADRANTS
//#define SHOW_GRID
//#define SHOW_HASH

#define BODY
#define EYES
//#define EYES_NONMIRROR
#define ARMS
#define BAG

//#define FUNNY_BUG


/******** TIME/INPUT-BASED PARAMETERS ********/
vec3 ORIGIN;
float LIGHTNING;
float LIGHTNING_STRIKE;
float CHARGE;

void initialize_globals()
{
    ORIGIN = vec3(8.*(1.4-iMouse.y/iResolution.y)*cos(.2*iTime), 1.2*iMouse.y/iResolution.y, 8.*(1.4-iMouse.y/iResolution.y)*sin(.2*iTime));
    LIGHTNING = (.6*smoothstep(.7, 1., (pow(abs(sin(.2*iTime + .3)), 80.))) + smoothstep(.7, 1., (pow(abs(sin(.2*iTime)), 100.))));
    LIGHTNING_STRIKE = smoothstep(.3, 1., (pow(abs(sin(.2*iTime -.13)), 100.)));
    CHARGE = smoothstep(0., .2, (pow(abs(sin(.2*iTime -.4)), 100.)));
}

/******** PARAMETERS ********/
const float VERY_LARGE = 1e30;
const float NORMAL_DELTA = .005;
const float STEP = .001;
const int MAX_STEPS = 100;
const float MAX_DIST = 40.;
const float MIN_DIST = .001;
const vec2 e = vec2(NORMAL_DELTA, 0);
const float PI = 3.1415;

const float MAX_SKY_DIST = 700.;
const float SKY_HEIGHT = 30.;
const float CELL_SIZE = 1.1;
const float POLE_AREA_RADIUS = 5.;


/******** MATERIALS ********/
float last_dist = VERY_LARGE;
float hattifnatter_dist = VERY_LARGE;
float pole_dist = VERY_LARGE;
float bag_dist = VERY_LARGE;
float bag_deco_dist = VERY_LARGE;
float ground_dist = VERY_LARGE;
float barometer_dist = VERY_LARGE;
float barometer_glass_dist = VERY_LARGE;
float lightning_dist = VERY_LARGE;
float min_lightning_dist = VERY_LARGE;
float min_hattifnatter_dist = VERY_LARGE;


const vec3 HATTIFNATTER_COLOR = vec3(204./256., 200./256., 192./256.);
const vec3 HATTIFNATTER_CHARGE_COLOR = vec3(196./256., 186./256., 163./256.);
const vec3 SKY_COLOR = vec3(138./256., 159./256., 202./256.);
const vec3 FOG_COLOR = SKY_COLOR*.5;
const vec3 GROUND_COLOR = vec3(71./256., 86./256., 53./256.);
const vec3 BAROMETER_COLOR = vec3(169./256., 89./256., 75./256.);
const vec3 BLACK_COLOR = vec3(27./256., 34./256., 35./256.)*.4;
const vec3 GOLD_COLOR = vec3(243./256., 202./256., 89./256.);

struct Material
{
    vec3 color;
    float rim_strength;
    float shininess;
};

const Material hattifnatter_mat = Material(HATTIFNATTER_COLOR, 1., 0.);
const Material pole_mat = Material(BLACK_COLOR, 0., 0.);
const Material bag_mat = Material(BLACK_COLOR, 0., 0.);
const Material bag_deco_mat = Material(GOLD_COLOR, 1., 50.);
const Material barometer_mat = Material(BAROMETER_COLOR, 1., 100.);
const Material barometer_glass_mat = Material(HATTIFNATTER_COLOR, 1., 80.);


/******** Rotations ********/
mat2 rot(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s,
               -s, c);
}

mat3 rotZ(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat3(c, s, 0,
               -s, c, 0,
                0, 0, 1);
}

mat3 rotY(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat3(c, 0, s,
                0, 1, 0,
               -s, 0, c);
}

mat3 rotX(float a)
{
    float c = cos(a);
    float s = sin(a);
#ifdef FUNNY_BUG
    return mat3(0, 0, 1,
#else
    return mat3(1, 0, 0,
#endif
                0,  c, s,
                0, -s, c);
}

/******** SDFs (most by Inigo Quilez) ********/

// 3D //
float sphere( vec3 p, float r )
{
  return length(p)-r;
}

float torus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float vertical_capsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

float rbox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b + r;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

// 2D //
float disk(vec2 p, float r)
{
    return length(p) - r;
}

float rrect( in vec2 p, in vec2 b, in float r )
{
    vec2 q = abs(p)-b+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}

// Operators //
float smin( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float extrudez(in float d, in vec3 p, in float h )
{
    vec2 w = vec2( d, abs(p.z) - h );
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}


/******** NOISE ********/
float hash1(vec2 uv)
{
    return fract(sin(dot(uv, vec2(43.3333, 53.8123))) * 42348.234);
}
float hash2(vec2 uv)
{
    return fract(sin(dot(uv, vec2(93.263434, 76.2134))) * 646881.3461);
}

float perlin(in vec2 uv)
{
    vec2 ij = floor(uv);
    vec2 t = smoothstep(0., 1., fract(uv));

    vec2 d = vec2(1, 0);
    float nw = hash1(ij);
    float ne = hash1(ij + d.xy);
    float se = hash1(ij + d.xx);
    float sw = hash1(ij + d.yx);

    return mix(mix(nw, ne, t.x), mix(sw, se, t.x), t.y);
}

float fbm(in vec2 uv)
{
    float s = 1.;
    float d = 1.;
    float n = perlin(uv);
    mat2 r = rot(17.3215);
    for (int i = 0; i < 8; i++)
    {
        s *= 2.;
        d *= .7;
        r *= r;
        n += perlin(s*r*uv) * d;
    }
    return n;
}

/******** SCENE ********/
float pattern(vec3 p, vec2 id)
{
    // Avoid rendering inside the pole area
    // and avoid oversteping based on this
    float origin_dist = length(id);
    if (origin_dist < POLE_AREA_RADIUS)
        return POLE_AREA_RADIUS - origin_dist;

    float r1 = hash1(id);
    float r2 = hash2(id);

    float h = .5 + r1*.5 + .1*sin(r1*.5*iTime+r2*13.);
    float arm_length = .2+r1*.1;
    float arm_height = max(.3, h*.6 - r2*.3);


    // Offset pos
    p.xz += .7*(vec2(1. - r1, r2)-.5);


#ifdef BAG
    // TODO make this its own function smh my head
    mat3 brot = rotY(r1*2.*PI);
    vec3 bp = rotY(r2*2.*PI) * p - vec3(.2, 0.1, .2);
    float bag = rbox(brot * bp, vec3(.06, .1, .12), .02);
    vec3 bdp = brot*(bp - vec3(0, .12, 0));
    float bag_deco = rbox(vec3(bdp.xy, abs(bdp.z)) - vec3(0, 0, .03), vec3(.02, .022, .01), .005);
    bag_deco = min(bag_deco, rbox(bdp - vec3(0, .02, 0), vec3(.02, .01, .04), .005));
#else
    float bag = VERY_LARGE;
    float bag_deco = VERY_LARGE;
#endif


    // Turn towards moving origin
    vec3 o = ORIGIN;
    p = rotY(atan(id.x + sign(o.x)*.5*CELL_SIZE - o.x, id.y + sign(o.z)*.5*CELL_SIZE - o.z)) * p;

    // Widen top
    float wavy = .4*p.x;
    // Sway
    wavy += .09*sin(2.*iTime+r1*13.)*p.y;
    // Wave
    wavy += .02*sin(iTime + (14.-h)*p.y + r2*23.);
    p = rotZ(wavy) * p;

    // Body
    float d = VERY_LARGE;
#ifdef BODY
    float hd = vertical_capsule(p, h, .1);
#else
    float hd = VERY_LARGE;
#endif

#ifdef EYES
    // Eyes (symmetry on the X axis)
#ifdef EYES_NONMIRROR
    hd = max(hd, -sphere(p - vec3(.04, h*.9, -.08), .03));
#else
    hd = max(hd, -sphere(vec3(abs(p.x), p.yz) - vec3(.04, h*.9, -.08), .03));
#endif
    // Pupils????
    //bag = min(bag, sphere(vec3(abs(p.x), p.yz) - vec3(.04, h*.9, -.06), .013));
#endif

#ifdef ARMS
    // Arms
    mat3 r = rotZ(.5*PI + (r1 - .5)*.2 - .2*PI);
    mat3 ri = rotZ(.1*PI);
    for (int i = -2; i <= 2; i++)
    {
        vec3 ap = r*(p - vec3(0, arm_height, 0)) - vec3(0, -arm_length, 0);
        hd = smin(hd, vertical_capsule(ap, 2.*arm_length, .015) , .04+r1*.04);
        if (i < 2) r = ri * r;
    }
#endif

    hattifnatter_dist = min(hattifnatter_dist, hd);
    d = min(d, hd);

    // The suitcase should be unaffected by swaying
    bag_dist = min(bag_dist, bag);
    bag_deco_dist = min(bag_deco_dist, bag_deco);

    d = min(d, min(bag, bag_deco));

    return d;
}

// From https://iquilezles.org/articles/sdfrepetition/
float repeat(vec3 p, vec2 s)
{
    vec2 p2d = p.xz;
    vec2 id = round(p2d/s);
    vec2 id_offset = sign(p2d-s*id);

    float d = VERY_LARGE;
    bag_dist = VERY_LARGE;
    bag_deco_dist = VERY_LARGE;
    hattifnatter_dist = VERY_LARGE;
    for (int y = 0; y < 2; y++)
    {
        for (int x = 0; x < 2; x++)
        {
            vec2 neighbour_id = id + vec2(x, y) * id_offset;
            vec2 grid_point = s * neighbour_id;
            vec3 real_p = p - vec3(grid_point.x, 0., grid_point.y);
            d = min(d, pattern(real_p, neighbour_id));
        }
    }

    return d;
}

float pole(vec3 p)
{
    pole_dist = vertical_capsule(p, 3., .2);

    // Barometer as extruded 2d surface
    p = rotY(.25*PI) * p;
    p.z = -p.z;
    p -= vec3(0, 2, -.249);
    float d2d = rrect(p.xy - vec2(0, -.1), vec2(.05, .3), .05);
    d2d = min(d2d, disk(p.xy - vec2(0, .15), .15));
    d2d = min(d2d, rrect(p.xy - vec2(0, -.2), vec2(.1, .05), .05));
    d2d = min(d2d, rrect(p.xy - vec2(0, -.3), vec2(.1, .05), .05));
    barometer_dist = extrudez(d2d, p, .04);
    p -= vec3(0, .15, -.02);
    barometer_glass_dist = extrudez(disk(p.xy, .1), p, .04);
    // Allow the glass to sink into the barometer surface w/o issue
    barometer_dist = max(barometer_dist, -barometer_glass_dist);

    return min(pole_dist, min(barometer_dist, barometer_glass_dist));
}

float dist(vec3 p)
{
    lightning_dist = VERY_LARGE;
    if (LIGHTNING_STRIKE > 0.)
    {
        float n1 = .4*(fbm(vec2(p.x, p.y*.5)+iTime) - 1.5);
        float n2 = .4*(fbm(vec2(p.x + 23., p.y*.5 - 35.)+iTime) - 1.5);
        float r = smoothstep(0., .8, LIGHTNING_STRIKE)*(.1 + .4*sqrt(max(0., 4. - p.y)));
        lightning_dist = vertical_capsule(p - vec3(n1,0,n2), SKY_HEIGHT, r);
    }

    // Bounding plane (cutting pole)
    if (p.y > 3.7)
         return min(lightning_dist, p.y + 3.5);

    float hattifnatter = repeat(p, vec2(CELL_SIZE));
    float pole = pole(p);
    ground_dist = p.y;

    return min(hattifnatter, min(lightning_dist, min(pole, ground_dist)));
}


/******** RAYMARCHING ********/
float march(vec3 origin, vec3 dir)
{
    float d = 0.;
    vec3 p = origin;
    min_lightning_dist = VERY_LARGE;
    min_hattifnatter_dist = VERY_LARGE;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        float step = dist(p);
        d += step;
        min_lightning_dist = min(min_lightning_dist, max(0., .01 + lightning_dist));
        min_hattifnatter_dist = min(min_hattifnatter_dist, max(0., hattifnatter_dist));
        if (step < MIN_DIST || d > MAX_DIST)
        {
            last_dist = step;
            break;
        }
        // understep slightly
        p += dir*step*.997;
    }

    return d;   
}

vec3 normal(vec3 p)
{
    return normalize(vec3(
        dist(p + e.rgg) - dist(p - e.rgg),
        dist(p + e.grg) - dist(p - e.grg),
        dist(p + e.ggr) - dist(p - e.ggr)
    ));
}


/******** COLOR ********/
float lightning_bolt()
{
    return LIGHTNING_STRIKE*max(0.,0.4*log(1./min_lightning_dist));
}

vec3 charge_glow()
{
    if (CHARGE <= 0.) return vec3(0);
    return CHARGE*HATTIFNATTER_CHARGE_COLOR*max(0.,0.1*log(1./min_hattifnatter_dist));
}

vec3 render_sky(vec3 ro, vec3 rd)
{
    float t = (SKY_HEIGHT - ro.y)/rd.y;
    vec3 base_color = SKY_COLOR * (.5 + LIGHTNING);
    float bolt = lightning_bolt();
    if (t <= 0. || t > MAX_SKY_DIST)
        return base_color;
    vec3 p = ro + t*rd;
    float fn = fbm(.2*p.xz);
    float noise = .3*fn + fn*LIGHTNING;
    return mix(SKY_COLOR * noise, base_color, smoothstep(100., MAX_SKY_DIST, t));
}

vec3 render_ground(vec3 p)
{
    vec3 base = GROUND_COLOR * (.2+.5*fbm(2.*p.xz));
    vec3 tint = vec3(1);
#ifdef SHOW_QUADRANTS
    tint *= vec3(int(p.z < 0.), int(p.x > 0.), int(p.z > 0. && p.x < 0.));
#endif
#ifdef SHOW_GRID
    vec2 idg = round(p.xz/CELL_SIZE);
    tint *= .3 + .7*mod(idg.x + idg.y, 2.);
#endif
#ifdef SHOW_HASH
    vec2 idh = round(p.xz/CELL_SIZE);
    tint *= hash1(idh);
#endif
    return base * tint;
}

vec3 light(vec3 p, vec3 n, vec3 l, vec3 v, Material m)
{
    vec3 col = vec3(0);
    // Light sources
    col += m.color * (.8 * max(0., dot(n, l)) + .5 + .2*LIGHTNING);
    col += float(m.shininess > 0.) * vec3(1) * pow(max(0., dot(reflect(l, n), -v)), max(.0001, m.shininess));
    col += SKY_COLOR * (0.4 * max(0., dot(n, -l)) + .2 + .1*LIGHTNING);
    col += vec3(1) * (LIGHTNING * max(0., dot(n, normalize(vec3(0, 40, 0)-p))));
    col += vec3(1) * (LIGHTNING_STRIKE * max(0., dot(n, normalize(vec3(0, 4, 0)-p))) );
    // Rim lighting
    float vdotn = dot(v, n);
    col = mix(col, m.color*1.2, m.rim_strength*smoothstep(.1, 0., max(0., vdotn*vdotn)));
    return col;
}

Material get_material(vec3 p)
{
    Material m = hattifnatter_mat;
    dist(p);
    if (hattifnatter_dist <= MIN_DIST)
        m = hattifnatter_mat;
    else if (pole_dist <= MIN_DIST)
        m = pole_mat;
    else if (bag_dist <= MIN_DIST)
        m = bag_mat;
    else if (bag_deco_dist <= MIN_DIST)
        m = bag_deco_mat;
    else if (barometer_dist <= MIN_DIST)
        m = barometer_mat;
    else if (barometer_glass_dist <= MIN_DIST)
        m = barometer_glass_mat;
    else
        m = Material(render_ground(p), 0., 0.);
    return m;
}

vec3 color(vec3 p, vec3 v, float d)
{
    // Regular lighting
    vec3 n = normal(p);
    vec3 l = vec3(.5, 1, .5);
    Material m = get_material(p);
    return mix(light(p, n, l, v, m), FOG_COLOR + LIGHTNING, smoothstep(.8, 4., log(d)));
}


/******** RENDER ********/
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    initialize_globals();

#ifdef IS_2D
#ifdef SINGLE
    // Hattifnatter cross section ðŸ’€
    uv.y += .5;
    vec3 p = vec3(uv, .1);
    float d = pattern(rotY(PI*.5) * (p + vec3(0, 0, .2)), vec2(9, 11));
    float f = 300.;
#else
    // Top-down SDF slice
    uv *= 20.;
    vec3 p = vec3(uv.x, .5 + .5*iMouse.x/iResolution.x, uv.y);
    float d = dist(p);
    float f = 60.;
#endif
    vec3 col = mix(vec3(.5,0,.1), vec3(1), smoothstep(.02, 0., d)) * (.5 + .8*cos(f*d));

#else
    // 3D scene >:)
    vec3 origin = ORIGIN + vec3(0, 1.2, 0);
    float face_angle = atan(origin.x, origin.z) - iMouse.x/iResolution.x*PI*2.;
    vec3 dir = rotY(-face_angle) * rotX(-.3*(iMouse.y/iResolution.y - .5) - .2) * normalize(vec3(uv.x, uv.y, -.7));
    float d = march(origin, dir);
    vec3 p = origin + dir*d;

    vec3 col;
    // Beyond (sky)
    if (d > MAX_DIST*.9 || lightning_dist <= MIN_DIST)
        col = render_sky(origin, dir);
    // Everything else
    else
        col = color(p, dir, d);
    col += lightning_bolt();
    col += charge_glow();
#endif

#ifdef SHOW_DEPTH
    col = vec3(1) * max(0.,1. - log(1./(.07*d)));
#endif

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float TAU = 6.2831;
const float CHAMBER_TONE = 440.;

/****** ADJUSTMENTS ******/
float env(float dec, float amp, float t)
{
    return exp(-dec*t)*amp;
}

vec2 pan(float p)
{
    return normalize(vec2(1.-p, 1.+p));
}

/****** INSTRUMENTS ******/
float fm(float fc, float fm, float iom, float t)
{
    return sin(TAU*fract(fc*t) + iom*sin(TAU*fract(fm*t)));
}

float stringFM(float f, float t)
{
    return fm(f, f*4., 1., t) * env(3., .3, t);
}

vec2 bassFM(float f, float t)
{
    vec2 sound = vec2(0);
    sound += fm(f-1., f*2., .7, t) * env(5., .2, t) * pan(-.5);
    sound += fm(f+1., f*2., .7, t) * env(5., .2, t) * pan(.5);
    sound += fm(f*.5, f*4., 1., t) * env(5., .2, t);

    return sound;
}


/****** NOTE STUFF ******/
float interval(float semitones)
{
    return pow(2., semitones/12.);
}

struct T {
    float semitones;
    float duration;
};


const float BPM = 95.;
const float SPB = 60./BPM;


/****** NOTES ******/
#define NOTES_IN_MELODY 10
const T melody[] = T[NOTES_IN_MELODY]
(
    T(0., .5), T(3., .5), T(2., .5), T(3., 4.5),
    T(0., .5), T(3., .5), T(2., .5), T(3., .5),
    T(0., 1.), T(0., 3.)
);

#define NOTES_IN_BASS 4
const T bass[] = T[NOTES_IN_BASS]
(
    T(-0., .5), T(3., .5), T(2., .5), T(3., .5)
);


/****** PLAYBACK ******/
vec2 play_melody(float t)
{
    vec2 sound = vec2(0);
    float nt = 4. * SPB;
    float duration = 4.;
    for (int i = 0; i < NOTES_IN_MELODY; i++)
    {
        duration += melody[i].duration;
    }
    for (int i = 0; i < NOTES_IN_MELODY; i++)
    {
        T note = melody[i];
        float f = CHAMBER_TONE * interval(note.semitones);
        sound += stringFM(f, mod(t - nt, duration*SPB));
        nt += note.duration * SPB;
    }
    return sound;
}

vec2 play_bass(float t)
{
    vec2 sound = vec2(0);
    float nt = 0.;
    float duration = 0.;
    for (int i = 0; i < NOTES_IN_BASS; i++)
    {
        duration += bass[i].duration;
    }
    for (int i = 0; i < NOTES_IN_BASS; i++)
    {
        T note = bass[i];
        float f = CHAMBER_TONE * interval(note.semitones-24.);
        sound += bassFM(f, mod(t - nt, duration*SPB));
        nt += note.duration * SPB;
    }
    return sound;
}


/****** OUTPUT ******/
vec2 mainSound( int samp, float t )
{
    vec2 sound = vec2(0);

    sound += play_melody(t);
    sound += play_bass(t);

    float lightning = (.6*smoothstep(.7, 1., (pow(abs(sin(.2*t + .3)), 80.))) + smoothstep(.7, 1., (pow(abs(sin(.2*t)), 100.))));
    sound += lightning*sin(TAU*CHAMBER_TONE*interval(-12.)*mod(t, 20.))*.6;
    float lightning_strike = smoothstep(.3, 1., (pow(abs(sin(.2*t -.13)), 100.)));
    sound += lightning_strike*cos(TAU*CHAMBER_TONE*interval(-4.)*mod(t, 20.))*.6;

    return sound;
}
