// Fork of "Inverse Radon Transformation" by wyatt. https://shadertoy.com/view/lfVGRV
// 2024-04-14 13:52:34

Main {
    float b = B(U).x;
    U.x -= .25*R.x;
    Q = vec4(0);
    Q.z  = length(C(U).xy)*50.;//1.*(1.-texture(iChannel3,vec2(-.45,.5)+2.*vec2(.1,.5)*(U/R.y*2.-1.)).y);//map(2.*(U-.5*R)/R.y,iTime);
    if (U.x<0.25*R.x)Q.z *= 0.;
    U.x += .5*R.x;
    U -= .5*R;

    U = U.yx;
    U.x *= R.x/R.y;
    U.y *= -1.;
    U += .5*R;
    U = floor(U);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        Q.xy += A(vec2(i+.5,U.y)).xy*ei(-(w.x-pi)*i)/sqrt(R.x);
    }

    //Q = A(U);
    Q.xy = length(Q.xy)*vec2(1);

    if (U.y<0.) Q.xy *= 0.;
    else 
    Q = abs(Q);

    Q += 4.*b;
    //Q = C(U);

    //Q = B(U);
}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) 

float line (vec2 p, vec2 a, vec2 b) { 
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.14159265359
vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float map (vec2 u,float t) {
    float o = 0.;
    if (u.x>-.1&&u.x<.1&&u.y>-.1&&u.y<.1)
        o += .5;

     if (length(u -.5*vec2(cos(t),sin(t)))<.5) o += .2;
     u.y += .5*sin(2.*t);
   if (u.x>.3&&u.x<.4&&u.y>.3&&u.y<.4)
        o += 1.;


    return o;

}

Main {
    #define N 500.

    float theta = U.x/R.x*pi;

    Q = vec4(0);

    for (float i = 0.; i < N; i++) {

        float r = i/N*2.-1.;

        vec2 u = vec2(r,U.y/R.y*2.-1.)*ei(theta);


        if (u.x < .8 && u.y < .8 && u.x>-.8 && u.y>-.8) {
            u.x -= float((iFrame/10)%5)*(1.561);
            vec4 t = texture(iChannel0, +vec2(-.14,.5)+vec2(.1,.5)*u);
            Q.x += .25*(t.z*t.w)/N;//map(u,iTime)/N;
        }
    }


    //Q.x = map(2.*(U-.5*R)/R.y);

}

Main {

    Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        Q.xy += A(vec2(U.x,i+.5)).xy*ei((w.y-pi)*i)/sqrt(R.y);
    }
    Q *= mod(U.y,2.)*2.-1.;
    //Q *= mod(floor(U.y*2.)/2.,2.)*2.-1.;

}

Main {
    #define N mod(float(iFrame/3),200.)


    Q = vec4(0);

    for (float i = 0.; i < N; i++) {

        float theta = i/N*pi;
        vec2 v = U - .5*R;
        vec2 u = vec2(i/N*R.x,.8*length(v));

        vec2 a = .5*R;
        vec2 b = (vec2(.5*R.y,0)*ei(theta));
        #define f(a) clamp(.05*a*sqrt(u.y/R.y),0.,1.)

        Q += vec4(A(vec2(u.x,.5*R.y+u.y+1.)).xyz,1)*(smoothstep(1.,0.5,line(U,a,a+b)));
        Q += vec4(A(vec2(u.x,.5*R.y-u.y)).xyz,1)*(smoothstep(1.,0.5,line(U,a,a-b)));
    }
    if (Q.w > 0.) Q/= Q.w;
    Q *= 50.;



}

Main {

    Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        Q.xy += A(vec2(U.x,i+.5)).xy*ei(-(w.y-pi)*i)/sqrt(R.y);
    }


}
