// -2 thanks to kishimisu ðŸŒŒ

void mainImage(out vec4 o, vec2 u) 
{
    float i, 
          j,
          t,
          s,
          d = 1.;

    vec3 R = iResolution,
         l = normalize(vec3(u+u-R.xy, 1.4 * R.y)),
         p,
         r;

    //l.xz *= mat2(cos(cos(iTime/3.)+vec4(0,11,33,0)));
    for (p.z = iTime/9.; i++ < 160. && d > 3e-5; t += d) 
    for (d = 0., s = j = 1.; 
         j++ < 9.;
         d = max(d, min(r.x-.082, min(r.y, r.z) -.016) / s))
        r = abs(fract(s * (p+l*t)) - .5),
        r.z += l.y*t,
        r.xy = sin(r.xy*1.75),
        r *= r,
        s += s;


    u /= R.xy;
    u *= 4.*u-4.;
    o = max(t*.5 * vec4(.6,.7,1,0), 1.-i/2e2)
      * pow(u.x * u.y, .1);
}
