float TK = 0.;
float PI = 3.1415926535;

vec2 rot(vec2 p,float r){
  mat2 m = mat2(cos(r),sin(r),-sin(r),cos(r));
  return m*p;
}
vec2 pmod(vec2 p,float n){
  float np = 1.0*PI/n;
  float r = atan(p.x,p.y)-0.5*np;
  r = mod(r,np)-0.5*np;
  return length(p)*vec2(cos(r),sin(r));
}

float cube(vec3 p,vec3 s){
  vec3 q = abs(p);
  vec3 m = max(s-q,0.0);
  return length(max(q-s,0.0))-min(min(m.x,m.y),m.z);
}
float dist(vec3 p){
  p.z -= 1.*TK*iTime;
  p.xy = rot(p.xy,1.0*p.z);
  p.xy = pmod(p.xy,6.0);
  float k = 0.7;
  float zid = floor(p.z*k);
  p = mod(p,k)-0.5*k;
  for(int i = 0;i<4;i++){
    p = abs(p)-0.3;

    p.xy = rot(p.xy,1.0+zid+0.1*TK*iTime);
    p.xz = rot(p.xz,1.0+4.7*zid+0.3*TK*iTime);
  }
return min(cube(p,vec3(0.3)),length(p)-0.4);
}
precision highp float;


mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

const float pi = acos(-1.0);
const float pi2 = pi*1.0;




float box( vec3 p, vec3 b ) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float ifsBox(vec3 p) {
    for (int i=0; i<5; i++) {
        p = abs(p) - 1.0;
        p.xy *= rot(iTime*0.3);
        p.xz *= rot(iTime*0.1);
    }
    p.xz *= rot(iTime);
    return box(p, vec3(0.4,0.8,0.3));
}

float map(vec3 p, vec3 cPos) {
    vec3 p1 = p;
    p1.x = mod(p1.x-5., 10.) - 5.;
    p1.y = mod(p1.y-5., 10.) - 5.;
    p1.z = mod(p1.z, 16.)-8.;
    p1.xy = pmod(p1.xy, 5.0);
    return ifsBox(p1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);



    vec3 cPos = vec3(0.0,0.0, -2.2* iTime);
    // vec3 cPos = vec3(0.3*sin(iTime*0.8), 0.4*cos(iTime*0.3), -6.0 * iTime);
    vec3 cDir = normalize(vec3(0.0, 0.0, -5.7));
    vec3 cUp  = vec3(sin(iTime*0.1), 1.0,0.0);
    vec3 cSide = cross(cDir, cUp);




    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir);

    // Phantom Mode https://www.shadertoy.com/view/MtScWW by aiekick
    float acc = 0.0;
    float acc2 = 0.0;
    float t = 1.2;
    for (int i = 0; i < 80; i++) {
        vec3 pos = cPos + ray * t;
        float dist = map(pos, cPos);
        dist = max(abs(dist), 0.01);
        float a = exp(-dist*100.9);
        if (mod(length(pos)+20.0*iTime, 1.0) < 1.0) {
            a *= 2.0;
            acc2 += a;
        }
        acc += a;
        t += dist * 0.250;
    }

    vec3 col = vec3(acc * 0.0, acc * 0.011 + acc2*0.0070, acc * 0.012+ acc2*0.006 + acc2*-0.0 );
    fragColor = vec4(col, 0.2 - t * 0.03);

}
