void mainImage(out vec4 c, vec2 v) {
    v = v / iResolution.y - 0.5;
    float u = length(v - vec2(0.1 * cos(iTime), 0.25));
    c.r = -fract(v.x += iTime * 0.3)       * fract(-v.x);
    c.g = -fract(v.x += v.x + iTime * 0.3) * fract(-v.x);
    c.b = -fract(v.x += v.x + iTime * 0.3) * fract(-v.x);
    c.a = -fract(v.x += v.x + iTime * 0.3) * fract(-v.x);
    v.x = smoothstep(-0.7, 0.3, dot(c, vec4(0.25)));
    c  = vec4(0.6, 0.8, 0.9, 1) * vec4(v.y * 5.1 + 1.3 < v.x);
    c += vec4(v.y * 9.1 + 0.9 > v.x && u > 0.02);
    c  = c.r == c.b? vec4(0.8, 1, 0.9, 1) : c;
    c  = v.y > -0.35? c : vec4(1.0, 0.9, 0.7, 1);
    c  = u < 0.1? vec4(1, 1, 0.5, 1) : c;
}
