vec2 hash(vec2 p)
{
    p = vec2(
        dot(p, vec2(127.1, 311.7)),
        dot(p, vec2(269.5, 183.3))
    );
    return fract(sin(p) * 18.5453);
}

// return distance
float voronoi(vec2 x)
{
    vec2 n = floor(x);
    vec2 f = fract(x);

	float m = 8.0;
    for(int j = -1; j <= 1; j++) {
        for(int i = -1; i <= 1; i++) {
            vec2 g = vec2(float(i), float(j));
            vec2 o = hash(n + g);
            vec2 r = g - f + (0.5 + 0.5 * sin(iTime * 2.5 + 6.2831 * o));
            float d1 = dot(r, r);
            float d2 = abs(r.x) + abs(r.y);
            float d = (sqrt(d1) + d2) / 2.0;
            m = d < m ? d : m;
        }
    }

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / max(iResolution.x, iResolution.y);

    // compute voronoi patterm
    float c = voronoi(20.0 * p);

    // colorize
    vec3 col = vec3(0.0, 1.0, 1.0) * (clamp(0.4 * c * c, 0.0, 1.0) * 0.65 + 0.1);	

    vec3 background = texture(iChannel0, fragCoord / iResolution.xy * 4.0).rgb;

    fragColor = vec4(background + col, 1.0);
}
