#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S2(d,b) smoothstep(1.,-1., (d - b) / fwidth(d) )
#define S(d,b) smoothstep(antialiasing(1.5),-antialiasing(1.5),d - b)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define deg45 .707
#define R45(p) (( p + vec2(p.y,-p.x) ) *deg45)
#define Tri(p,s) max(R45(p).x,max(R45(p).y,B(p,s)))
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )

float arrow(vec2 p, float scale, int outline){
    p*= scale;
    vec2 prevP = p;
    float s = 0.1;
    p*=vec2(1.25,0.5);
    p.y-=s*0.5;
    float d = Tri(p,vec2(s));
    p-=vec2(0.,-s*0.5);
    float d2 = Tri(p,vec2(s));
    if(outline == 1)d = abs(max(-d2,d))-0.005;
    p = prevP;
    d = max(-p.y-s,d);
    return d;
}

float animationArrow(vec2 p, float speed){
    vec2 prevP = p;
    p.y-=iTime*0.1*speed;
    p.y+=0.06;
    p.y = mod(p.y,0.12)-0.06;
    float d = arrow(p,2.,1);
    p = prevP;
    d = max(arrow(p*0.85,2.,0),d);
    return d;
}

float arrows(vec2 p, float dir, int num, float speed){
    p*=dir;
    for(int i = 0; i<num; i++){
        p.x = abs(p.x)-0.05;   
    }
    float d = animationArrow(p,speed);
    return d;
}

float baseLayer(vec2 p, float y, int num, float speed){
    vec2 prevP = p;
    p = DF(p,2.);
    p-=0.12+y;
    p*=Rot(radians(45.));
    float d = arrows(p,1.,num,speed);
    p = prevP;
    p = DF(p,2.);
    p-=0.14+y;
    p*=Rot(radians(45.));
    float d2 = arrows(p,-1.,num+1,speed);
    d = min(d,d2);
    return d;
}

float drawMainGraphics(vec2 p, float animation){
    vec2 prevP = p;
    float d = baseLayer(p,0.,0,0.5*animation);
    for(int i = 1; i<3; i++){
        float d2 = baseLayer(p,0.1*float(i),i,1.*float(i)*0.5*animation);
        d = min(d,d2);
    }
    return d;
}

float triGrids(vec2 p){
    p.x*=1.2;
    vec2 prevP = p;

    p.y-=iTime*0.05;
    float s = 0.06;
    vec2 q = mod(p,s)-(s*0.5);
    vec2 prevQ = q;

    q.y = abs(q.y)-(s*0.5);
    float d = abs(Tri(q,vec2(s*0.5)))-0.001;
    q = prevQ;
    q.y+=s*0.5;
    float d2 = abs(q.y)-0.002;
    d = min(d,d2);

    p = prevP;
    d = max(Tri(p,vec2(0.24)),d);

    float a = radians(45.);
    p.x+=0.028;
    d = max(dot(p,vec2(cos(a),sin(a))),d);

    p = prevP;
    a = radians(-45.);
    p.x-=0.028;
    d = max(-dot(p,vec2(cos(a),sin(a))),d);

    p = prevP;
    p.y+=0.03;
    d2 = abs(Tri(p,vec2(0.21)))-0.003;
    d = min(d,d2);
    return d;
}

float drawOuterGraphics(vec2 p){
    vec2 prevP = p;
    p = DF(p,2.);
    p-=0.525;
    p*=Rot(radians(45.));
    float d = triGrids(p);
    return d;
}

float drawCenterGraphics(vec2 p){
    vec2 prevP = p;
    p = DF(p,2.);
    p-=0.025;
    p*=Rot(radians(45.));
    float d = abs(B(p,vec2(0.045)))-0.003;
    p = prevP;
    float d2 = abs(B(p,vec2(0.03,0.08)))-0.003;
    d = min(d,d2);
    d2 = abs(B(p,vec2(0.08,0.03)))-0.003;
    d = min(d,d2);
    return d;
}

float stripes(vec2 p, float dir){
    vec2 prevP = p;

    p*=Rot(radians(30.));
    p.x+=iTime*0.01*dir;
    p.x = mod(p.x,0.02)-0.01;

    float d = B(p,vec2(0.005,10.));
    return d;
}

float stripeTriangle(vec2 p, float size, float dir){
     vec2 prevP = p;
     float d = Tri(p,vec2(size));
     d = max(stripes(p,dir),d);
     return d;
}

float drawFarOuterGraphics(vec2 p){
    vec2 prevP = p;
    p*=Rot(radians(23.));
    p = DF(p,2.);
    p-=0.38;
    p*=Rot(radians(45.));
    p.x*=0.51;
    p.y*=-1.;
    float d = stripeTriangle(p,0.13,1.);
    p = prevP;

    p*=Rot(radians(23.));
    p = DF(p,2.);
    p-=0.575;
    p*=Rot(radians(45.));
    p.x*=0.51;
    float d2 = stripeTriangle(p,0.13,-1.); 
    d = min(d,d2);

    p = prevP;

    p = DF(p,2.);
    p-=0.525;
    p*=Rot(radians(45.));
    p.x*=0.08;
    p.y*=-1.;
    d2 = Tri(p,vec2(0.025));    
    d = min(d,d2);

    p = prevP;

    p = DF(p,2.);
    p-=0.66;
    p*=Rot(radians(45.));
    p.x*=0.51;
    d2 = stripeTriangle(p,0.15,1.);  
    d = min(d,d2);

    return d;
}

float centerMask(vec2 p){
    vec2 prevP = p;
    p = DF(p,2.);
    p-=0.265;
    p*=Rot(radians(45.));
    float d = B(p,vec2(0.4));
    p = prevP;
    p = DF(p,2.);
    p-=0.66;
    p*=Rot(radians(45.));
    p.x*=0.48;
    float d2 = Tri(p,vec2(0.165)); 
    d = min(d,d2);

    return d;
}

float drawBg(vec2 p){
    vec2 prevP = p;
    float l = 0.0012;
    p = mod(p,0.06)-0.03;
    float d = min(abs(p.x)-l,abs(p.y)-l);
    p = prevP;
    float d2 = centerMask(p*0.98);
    d = max(-d2,d);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv*=1.45;
    vec3 col = vec3(0.);

    float d = drawMainGraphics(uv*-1.1,0.);
    col = mix(col,vec3(0.3),S(d,0.0));

    d = drawMainGraphics(uv*-1.1,1.);
    col = mix(col,vec3(1.),S(d,0.0));

    d = drawOuterGraphics(uv);
    col = mix(col,vec3(1.),S(d,0.0));

    d = drawCenterGraphics(uv);
    col = mix(col,vec3(1.),S(d,0.0));    

    d = drawFarOuterGraphics(uv);
    col = mix(col,vec3(1.),S(d,0.0));

    d = drawBg(uv);
    col = mix(col,vec3(1.),S(d,0.0));

    fragColor = vec4(col,1.0);
}
