float f_n_lin_inout(float n_t){
    //https://www.desmos.com/calculator/3a7uhp3om5
    float n_tau = radians(360.);
    n_t = fract(n_t/n_tau);
    return max(min(n_t*4., -(n_t-.5)*4.), (n_t-1.)*4.);
}
float f_n(
    vec2 o_trn, 
    float n_t
){
    float n_tau = radians(360.);

    o_trn *= 1.+(sin(n_t)*.5+.5)*2.;
    o_trn += vec2(sin(n_t*.2), cos(n_t*3.))*.02;
    float nl = length(o_trn);
    float n = nl;
    float n_f = (n*3.+n_t)+.5;

    float n_id = fract(floor(n_f/n_tau) /3.);
    float n_ang = n_tau*sin(n*2.+n_t)*.1;
    float ns = sin(n_ang);
    float nc = cos(n_ang);
    vec2 o2 = abs(mat2(nc, -ns, ns , nc)*o_trn);
    //vec2 o2 = abs(o_trn);
    n = max(o2.x, o2.y);
    n = f_n_lin_inout( n*(1./nl)*9.+n_t)*.5+.5;
    float b = 0.01+(sin(nl*3.+n_t*3.)*.5+.5)*.2;
    n = -(b/(n-1.-b));

    float n2= length(o_trn);


    n2 = f_n_lin_inout(n2*22.+n_t)*.5+.5;
    n2 = -(b/(n2-1.-b));

    float n3 = n+n2;
    //n3 = pow(n3, 1./2.);
    //n3 = sin(n3*3.*n_tau);
    //n3 = pow(n3, 2.);
    return n3;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_res = iResolution.xy;
    float n_scl_min = min(o_res.x, o_res.y);
    vec2 o_trn = (fragCoord.xy-o_res*.5)/n_scl_min;
    vec2 o_trn_mouse = iMouse.xy/iResolution.x;


    float nt = iTime;
    fragColor = vec4(
        f_n(o_trn, nt+.1),
        f_n(o_trn, nt+.2),
        f_n(o_trn, nt+.3), 
        1.
    ); 

