#define pi 3.1415926

struct material {
    vec3 colour;
    float roughness;
    float metallic;
};

const material base = material(vec3(1), 1., 0.);
material grille = material(vec3(.1), 1., 1.);
material paint = material(vec3(.2, .3, .9), 1., 1.);
material tire = material(vec3(.2), 1., 0.);
material glass = material(vec3(0), 0., 0.);
material mirror = material(vec3(1), 0., 1.);
material carbon_fibre = material(vec3(.4), 1., 0.);

material mat = base;



float torusDF( vec3 p, vec2 t ) // By Inigo Quilez
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float boxDF( vec3 p, vec3 b ) // By Inigo Quilez
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float rectDF( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float triangleDF( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}


float cylinderDF( vec3 p, float h, float r ) // By Inigo Quilez
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r,h);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float smin( float a, float b, float k ) // By Inigo Quilez 
{
    k *= 1.0;
    float ra = exp2(-a/k) + exp2(-b/k);
    return -k*log2(ra);
}

float smax( float a, float b, float k ){
    return -smin(-a, -b, k);
}

mat2 rot(float t){
    return mat2(
        cos(t), sin(t), -sin(t), cos(t)
    );
}

float lagrange(float x){
    vec2 a = vec2(.063, .076);
    vec2 b = vec2(.595, .192);
    vec2 c = vec2(.817, .173); // was .183
    vec2 d = vec2(.323, .106);
    vec2 e = vec2(1.00, .161);

    float l1 = (x*(x-b.x)*(x-c.x)*(x-d.x)*(x-e.x)*a.y) / (a.x*(a.x-b.x)*(a.x-c.x)*(a.x-d.x)*(a.x-e.x));
    float l2 = (x*(x-a.x)*(x-c.x)*(x-d.x)*(x-e.x)*b.y) / (b.x*(b.x-a.x)*(b.x-c.x)*(b.x-d.x)*(b.x-e.x));
    float l3 = (x*(x-a.x)*(x-b.x)*(x-d.x)*(x-e.x)*c.y) / (c.x*(c.x-a.x)*(c.x-b.x)*(c.x-d.x)*(c.x-e.x));
    float l4 = (x*(x-a.x)*(x-b.x)*(x-c.x)*(x-e.x)*d.y) / (d.x*(d.x-a.x)*(d.x-b.x)*(d.x-c.x)*(d.x-e.x));
    float l5 = (x*(x-a.x)*(x-b.x)*(x-c.x)*(x-d.x)*e.y) / (e.x*(e.x-a.x)*(e.x-b.x)*(e.x-c.x)*(e.x-d.x));

    return l1 + l2 + l3 + l4 + l5;

}

#define n(x) m=min(m, x)

//Chassis dimensions
vec3 offset = vec3(3.6, 0, 1);
vec3 size = vec3(6.5, 2.9, 1.7);
float grilleScale = 25.;

float carDF(vec3 p, float steer, bool matUpdate){
    float m = 1e9;

    p.z -= 1.;
    p.xy = vec2(-p.y, p.x);

    float bb = boxDF(p - offset, size);
    if (bb > .5){
        return bb;
    }

    float ratio = .3;

    if (abs(p.y) > size.y - 1.5 && abs(p.x - 3.5) > 2. && abs(p.x - 3.5) < 4.7){

        //return 1.;

        // One wheel call for all four wheels
        vec3 w = (p - vec3(p.x > 4.?7.:0., p.y>0.?(size.y-.5):-(size.y-.5), 0)); 
        w.xy *= rot(p.x > 4.?0.:steer);

        float tread, surf = w.x*w.x + w.z*w.z;
        if (surf > .9) tread = pow(sin(w.y*70. - sin(atan(w.z, w.x)*50.))*.2+.2, 4.);

        n(
            smax(
                torusDF(w * vec3(1, ratio, 1), vec2(1.-ratio, ratio)),
                abs(w.y) - .5,
                .05
            ) + (surf > .9?
            tread*max(0., (1.-pow(w.y*3., 2.)))
            :0.)
        );

    }
    //if (dot(p, vec3(sin(iTime), cos(iTime), 0)) < 0.) return 1.;
    //if (p.y < 0.) return 1.;
    p.y = abs(p.y);
    // Everything after this point is mirrored


    // Chassis
    n(
        max(
            max(
                min(
                    smax(
                        smax(
                            smax(
                                p.y - size.y - (
                                    (lagrange((p.x - offset.x + size.x)/(size.x*2.)) - .12)*5.
                                        * exp(-p.x*.1+.5)
                                        - exp(p.x/1.5 - offset.x - size.x + 3.)
                                        - p.z*p.z*.2
                                            * min((1.-smoothstep(5., 10., p.x)), smoothstep(0., 5., p.x))
                                ), // Side

                                p.z + size.z - offset.z - (
                                    lagrange((p.x - offset.x + size.x)/(size.x*2.))*10. + 1.2
                                ), // Roof

                                .2
                            ),

                            // Floor
                            -(p.z + size.z - offset.z) + (
                                exp((p.x - size.x*1.54)*1.2)
                                //pow(max(0., (p.x-size.x))/3., 2.)
                            ),

                            .05
                        ),

                        bb,

                        .02
                    ),

                    min(
                        // Diffuser
                        boxDF(
                            (p - offset - vec3(size.x*.75, min((floor(p.y) + .65), 1.7), -1.)),
                            vec3(size.x/4., .03, size.z-1.)
                        ) - .02,

                        // Side Mirrors
                        max(
                            length((p - vec3(3, size.y - .3, .8 + p.x*.2))/vec3(3, 2, 1)) - .2,
                            p.x-3. - p.y*.1 + .2
                        )
                    )

                ),

                // Wheel wells
                -cylinderDF(p - vec3(p.x>3.5?7:0,size.y - .5,0), 1., 1.1)
            ),

            // Grille
            -(
                boxDF((p - vec3(-size.x + offset.x - .45, 0, -.4)), vec3(0, 1, .4))-.5
            )
        )
    );

    n(
        // Front splitter
        max(
            abs(p.z + size.z - offset.z)-.03,
            max(
                max(p.x + 1., p.x - 4.3 + p.y*2.), 
                -p.x - size.x + offset.x - .25 + exp((p.y-size.y+.24)*3.)
            )
        )
    );

    if (matUpdate){

        mat = paint;

        if (
            p.x < 0. 
            && rectDF(p.yz - vec2(0, -.3), vec2(1, .3)) < .2 && p.z > -.66 // Front Grille
        ) mat = grille;

        if (
            p.z > 0. 
            && rectDF(p.yx - vec2(-p.x*.3 + .9, 3.3), vec2(size.y*.7, 1)) < -(2. - p.x)/8. // Windshield
        ) mat = glass;

        if (
            rectDF(p.xz - vec2(5.3, 2), vec2(2, .3)) < .2 // Side window
            && p.z < 2.4-pow((p.x - 6.3)/3.7, 2.) // Main top curve
            - exp((p.x - 9.)/2.) // Upper rear curve
        ) mat = glass;

        if (
            abs(m - (p.x-3. - p.y*.1 + .2)) < .01
            && length((p.yz - vec2(size.y - .3, .8 + p.x*.2))/vec2(2, 1)) < .19 // Mirror surface
        ) mat = mirror;

        float c = cylinderDF(p - vec3(p.x>3.5?7:0,size.y - .5,0), 1., 1.1);
        if (
            p.z < -size.z + offset.z + .0301 + exp((p.x - size.x*1.54)*1.2) // Diffuser and splitter
            || abs(c-m) < .001 // Wheel wells 
            || p.x > size.x + offset.x
        ){
            mat = carbon_fibre;
            vec3 cp = p*10.;
            mat.colour *= mod(floor(cp.x) + floor(cp.y) + floor(cp.z), 2.) * .2 + .3; 
        }

        if (c < -.01) mat = tire;

        // Decal art
        if (
            p.z > 0. && length(p.xy) < .9 + .1*sin(atan(p.y, p.x)*15.)){
            mat.colour = vec3(1);
        }
    }


    return m;
}


