        if (length(p) > 30. && p.z > 1.) 
            mat.colour = .5 + .5*sin((p.x + p.y + p.z)/5. + vec3(0, 2, 4));

    } else {

        n(p.z);
        if (length(p) > 30. && length(p) < 70. && p.z > 1.){
            n(length(mod(p, 5.) - 2.5 + vec3(sin(p.z/3.), cos(p.y/3.), sin(p.x/3.)))-1.);
        }

    }

    return m;
}

float df(vec3 p){
    return df(p, false);
}

vec3 norm(vec3 p, float e){
    float c = df(p);
    return normalize(vec3(
        c - df(p - vec3(e, 0, 0)),
        c - df(p - vec3(0, e, 0)),
        c - df(p - vec3(0, 0, e))
    ));
}



