const int aoIter = 4;
const float aoDist = 0.05;
const float aoPower = 2.;

float ao(vec3 p, vec3 n) {
    float dist = aoDist;
    float occ = 1.0;
    for (int i = 0; i < aoIter; ++i) {
        occ = min(occ, df(p + dist * n) / dist);
        dist *= aoPower;
    }
    occ = max(occ, 0.0);
    return occ;
}


void mainImage( out vec4 O, vec2 U ){

    vec2 r = iResolution.xy;
    vec2 uv = U/r;
    vec2 cuv = (2.*U-r)/r.y;
    vec2 muv = (2.*iMouse.xy-r)/r.y + (vec2(iMouse.z > 0.?0.:min(iTime*.1, .05*iTime*iTime), -.5));
    muv.x *= 2.;
    O = vec4(0);

    vec3 o = vec3(0, -3.5, .5);
    vec3 dir = vec3(sin(muv.x)*cos(muv.y), cos(muv.x)*cos(muv.y), sin(muv.y));
    o -= dir*22.;
    vec3 right = normalize(cross(dir, vec3(0, 0, 1)));
    vec3 up = cross(right, dir);

    vec3 dirV = normalize(dir * 5. + right * cuv.x + up * cuv.y);

    int steps = 0;
    float d,tt,t=0.;
    vec3 p = o;
    bool hit = false;

    for (; steps < 256; steps++){
        p = o + dirV * t;
        d = df(p);
        t += d*.8;

        if (d < 1e-2){
            steps++;
            hit = true;
        }
    }
    tt = t;
    vec3 normal = norm(p, .5*t/r.y);

    float ao = mix(1., ao(p, normal), .5);
    // ~~Stolen~~ Permanently borrowed from https://www.shadertoy.com/view/XlXyD4

    vec3 col, rcol = vec3(0);
    vec3 pLight = -offset.yxz + vec3(cos(iTime), sin(iTime), 1.)*20.;
    float light = max(0.01, 100. * (dot(normal, (pLight-p)/pow(length(pLight-p), 3.))) + .1 ) * ao;
    df(p, true); 

    material omat = mat;

    float fresnel = 1.-pow(-dot(normal, dirV), 5.);

    if (mat.roughness < .5){

        o = p;
        dirV = reflect(dirV, normal);
        t = 0.1;

        bool rhit = false;
        for (steps = 0; steps < 128; steps++){
            p = o + dirV * t;
            d = df(p)/min(1., t);
            t += d*.8;

            if (d < 1e-2){
                steps++;
                rhit = true;
            }
        }
        tt+=t;

        vec3 pLight = vec3(-1, 1, 1.)*10.;
        df(p, true); 

        rcol = mat.colour * max(0.01, 100. * (dot(normal, (pLight-p)/pow(length(pLight-p), 3.))) + .1 );
        if (!rhit) rcol = vec3(0);//texture(iChannel1, dirV).xyz;


        if (omat.metallic > .5){
            col = omat.colour * rcol;
        } else {
            col = mix(omat.colour * light, rcol, fresnel);
        }
    } else {

        if (omat.metallic > .5){

            col = omat.colour * texture(iChannel0, normal.yzx).xyz;

        } else {
            col = omat.colour * light;
        }

    }
    if (!hit) col = vec3(0);//texture(iChannel1, dirV).xyz;


    //O.xyz = vec3(t);
    O.xyz = col;

    //O += vec4(1,0,0,0)*5e-3/(p.y*p.y);
    //O += vec4(0,1,0,0)*5e-3/(p.x*p.x);

    p.xy += offset.yx;
    O += vec4(1,.1,0,0) * (p.z < .1?5./pow(abs(rectDF(p.yx, size.xy-2.)), 2.):0.);

    O += vec4(0,.1,1.,0) * (p.z < .1?5./pow(abs(length(p.yx)-size.x*2.), 2.):0.);

    // the way this works is so absurdly hacky it took me a minute to figure out how it was still working
    // IN THE REFLECTIONS after adding reflections. Not totally accurate, but I'm not complaining XD

    O.xyz = flim(O.xyz, 0., true);
}

/*-----------------------------------------------

flim - Filmic Color Transform

Input Color Space:   Linear BT.709 I-D65
Output Color Space:  Linear BT.709 I-D65 / sRGB 2.2 (depends on arguments)

Description:
  Experimental port of flim for GLSL/Shadertoy
  matching flim v1.1.0.

Author:
  Bean (beans_please on Shadertoy)

Minified by:
  Ahsen (01000001 on shadertoy)

Original Repo:
  https://github.com/bean-mhm/flim

Original Shader:
