float trwv(float fl)
{
    return abs(fl-floor(fl+0.5));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(sin(uv.x*iTime),sin(uv.y*iTime),sin(length(uv*iTime)));

    vec3 col2 = vec3(sin(uv.x*2.*iTime),sin(uv.y*2.*iTime),sin(length(uv*2.*iTime)))+vec3(0.3,0.4,0.5);
    vec3 col3 = vec3(sin(uv.x*3.*iTime),sin(uv.y*3.*iTime),sin(length(uv*3.*iTime)))+vec3(-0.2,-0.4,-0.8);

     vec3 res=vec3(trwv(col.x+col2.x),trwv(col.y+col2.y),trwv(col.z+col2.z));
     res=vec3(trwv(res.x+col3.x),trwv(res.y+col3.y),trwv(res.z+col3.z));
    // Output to screen
    fragColor = vec4(res,1.0);
}
