#define tilesize 16.
#define borderwidth 0.06
float tile(vec2 fragCoord, float x, float y)
{
    vec2 uv = fract(fragCoord/tilesize);

    float yval = mix(uv.y, (1. - uv.y), step(0.0, -y));
    float xval = mix(uv.x, (1. - uv.x), step(0.0, -x));;


    return smoothstep(1.0 - borderwidth, 1.0 + borderwidth, yval + xval);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy * 2.) - vec2(1);

    #define sc (floor((fragCoord - iResolution.xy / 2.) / tilesize) + vec2(1))

    #define transittime 0.1
    #define timemod(x) smoothstep(-transittime, transittime, sin(iTime* x))
    #define timemod2(x, y) smoothstep(-transittime + y, transittime + y, sin(iTime* x))

    float xfunc = sin(sc.x*(timemod2(0.8, 0.7)*cos(sc.y*0.1) + timemod(1.)*0.2 + timemod(0.25)*0.0 + 0.5));
    float yfunc = sin(sc.y*(timemod(1.2)*0.24 + timemod(0.466)*0.3 + timemod2(0.1, 0.9)*cos(sc.x*0.1 + sc.y*0.1) + 0.5));

	fragColor = vec4(tile(fragCoord - iResolution.xy / 2., xfunc, yfunc)*0.9);
}
