// Perlin noise version of https://www.shadertoy.com/view/ltG3Dh
// for spacial interpolation issue, see https://www.shadertoy.com/view/MlGGW1

// --- noise from from IQ's https://www.shadertoy.com/view/XdXGW8
vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise(  vec2 p )
{
    vec2 i = floor( p ), f = fract( p ), u = f*f*(3.-2.*f);
    float v = mix( mix( dot( hash( i + vec2(0,0) ), f - vec2(0,0) ), 
                     dot( hash( i + vec2(1,0) ), f - vec2(1,0) ), u.x),
                mix( dot( hash( i + vec2(0,1) ), f - vec2(0,1) ), 
                     dot( hash( i + vec2(1,1) ), f - vec2(1,1) ), u.x), u.y);
    return v;
    //return 1.-abs(2.*v-1.);
    //return abs(2.*v-1.);
}
// ---


void mainImage( out vec4 O, vec2 uv )
{
    vec2 R = iResolution.xy,
         U = uv/R;
    O-=O;

    float l = (.5-.5*cos(iTime))*6., f=fract(l);                 // noise scaling
    if (U.x<.5)
        O += noise((uv-R/2.)/exp2(l));
    else
      //O += mix(noise(uv/exp2(floor(l))),noise(uv/exp2(ceil(l))),f);
        O += mix(noise(uv/exp2(floor(l))),noise(uv/exp2(ceil(l))),f)/sqrt(2.*(f*f-f)+1.); // + contrast-preserving
      //O += mix(noise(uv/exp2(floor(l/.1)*.1)),noise(uv/exp2(ceil(l/.1)*.1)),f);

    //O = 1.-abs(2.*O-1.);
    //O = abs(2.*O-1.);
    O = vec4(.5+.5*sin(20.*(U.x+U.y)+O.x));                            // marble pattern
    O = sqrt(O);                                                       // gamma correction
}
