float createSquareOld(float x1, float x2, float y1, float y2, vec2 coord)
{
    vec2 bl = step(vec2(x1,x2), coord);
    float pct = bl.x * bl.y;

    vec2 tr = step (vec2(y1,y2), 1.0 - coord);
    pct *= tr.x * tr.y;

    return pct;
}

float createSquare(float x1, float x2, float y1, float y2, vec2 coord)
{
    vec2 bl = clamp((coord-vec2(x1,x2))*iResolution.y,0.0,1.0);
    float pct = bl.x * bl.y;

    vec2 tr = clamp(((1.0-coord)-vec2(y1,y2))*iResolution.y,0.0,1.0);
    pct *= tr.x * tr.y;

    return pct;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

mat2 squeeze(float _amount){
 	return mat2 (_amount, 0,
                 0, 1./_amount);
}

mat2 shearx(float _amount){
   	return mat2 (1., 0,
                 _amount, 1.);
}

mat2 sheary(float _amount){
   	return mat2 (1., _amount,
                 0, 1.);
}

mat2 stretchx(float _amount){
    return mat2 (_amount, 0,
                 0, 1.);
}

mat2 stretchy(float _amount){
    return mat2 (1., 0,
                 0, _amount);
}


