// see also https://www.shadertoy.com/view/XlyyRy

float l = 5., e = .05, s = .1;  // mobiles maxsize, thickness, offset  

float bars( vec3 q, float l) {
    vec3  a  = abs( q - vec3(l,l,0) ) - e,         // dist to +x and +y sides
          a0 = abs(q) - vec3(l,l,0)   - e;         // dist to full square frame
    float dx = max(max(a.x,a0.y),a0.z),            // side +x
          dy = max(max(a0.x,a.y),a0.z);            // side +y
    return min(dx,dy);                             // union
}

#define rot(a) mat2( cos(a+vec4(0,33,11,0)) )      // https://www.shadertoy.com/view/XlsyWX
#define rot45 q.xy = vec2(q.x+q.y,q.x-q.y) / sqrt(2.)

void mainImage(out vec4 O, vec2 U) {

    float t = iTime*.1, t0;  if (iResolution.y<200.) t-=.95; // icon
    mat2  R = rot(t);      
    vec3  q = iResolution,
          D = normalize(vec3(.3*(U+U-q.xy)/q.y, -1)), // ray direction
          p = 25./q, a,a0;                            // marching point along ray

    O-=O; t = 1e9;
    for ( O++; O.x > .0 && t > .01 ; O-=.03 ) {    // ray-marching (indeed, sphere-marching)

        q  = p;
        t0 = q.z+7.;                               // background (optimization)
      //q.yz *= rot(-.2);                          // slant 
        q.xz *= R;                                 // global rotation
        rot45;                                     // make diag vertical
        t = min(t0, bars(-q,l));                   // root branche

        for (float i=0.; i<l; i+=s) {   
            t = min(t, bars(q,l-i));               // branche i

            q -= vec3(s,s,0);                      // offset to next part
            rot45;                                 // to diag axis
            q.xz *= R;                             // rot
            rot45;                                 // undo diag (here, equivalent)
        }

        p += t*D;                                  // step forward = dist to obj
    }
    if (t==t0) O-=O, O.b+=.2;                      // background
}
