// --- tracking a grouped object using the moments in the image
// almost free version (thanks MIPmap) of https://shadertoy.com/view/XtG3WK

// see also: better working CubeMap variant ( because power-of-2 â†’ MIPmap not bugged ) : https://www.shadertoy.com/view/XlcyR2

#define A(ch)  textureLod(ch, vec2(.5), 99.)     // get the sum on the whole image

void mainImage( out vec4 O,  vec2 U )
{
    float I = A(iChannel1).z, r, det;            // I average
    vec2  R = iResolution.xy, u = U / R,
          M = A(iChannel1).xy / I,               // shape barycenter: E(x),E(y)
         M2 = A(iChannel2).xy / I;               // M2 matrix: E(xx), E(yy)
          r = A(iChannel2).z  / I;               //            E(xy)

	M2 = M2 - M*M; r = r - M.x*M.y;              // centered moments
  //mat2 cov = mat2(M2.x, r, r, M2.y),           // covariant matrix
  //    icov = inverse( cov );
    det = M2.x*M2.y-r*r ;
    mat2 icov = mat2(M2.y, -r, -r, M2.x) / det;  // inverse

    O = texture(iChannel0, u );                               // draw image
    u *= 5.; if (fract(u)==u) O = texture(iChannel0, u).aaaa; // draw stencil (flech)
    U -= M;  if (dot( U, icov * U ) < 5. ) O.r++;             // draw covariant ellips ( quadratic form )	
    u.y-=4.; if (u.x<1.&&u.y>0.) O = texture(iChannel0, M/R+(u-.5)*6.*(sqrt(M2.x)+sqrt(M2.y))/2./sqrt(R.x*R.y) ).aaaa;
  //u.y-=4.; if (fract(u)==u) O = texture(iChannel0, M/R+(u-.5)*15./sqrt(5.*R.x*R.y*M2.yx/det) ).aaaa;
}                                                             // recentered rescaled

// --- source video

void mainImage( out vec4 O,  vec2 U ) {
    U /= iResolution.xy;
    O   = texture(iChannel0,U);            // source image
    O.a = 5.*max(0., O.r - O.g );          // stencil to analyze ( ~= flech )
}

// --- compute M1 moments E(x),E(y) and M0=E(I) in ultimate MIPmap LOD. (M1 to be normalized by M0) 

void mainImage( out vec4 O, vec2 U ) {
    O = vec4( U, 1, 0 ) * texture(iChannel0,U/iResolution.xy ).a;
}

// --- compute M2 matrix E(xx),E(yy),E(xy) in ultimate MIPmap LOD (to be normalized by Imean)

void mainImage( out vec4 O, vec2 U ) {   
    O = vec4( U*U, U.x*U.y, 0 ) * texture(iChannel0,U/iResolution.xy).a;
}
