float pInt(vec2, float);
float pFloat(vec2, float);
float char(vec2, int);
#define spc  U.x-=.5;
#define A(c)  U.x-=.5; O+= char(U,c);
#define C(c)  U.x-=.5; O+= char(U,64+c);
#define L(c)  U.x-=.5; O+= char(U,64+32+c);

void mainImage( out vec4 O, vec2 U )
{
    vec2 R =  iResolution.xy, V, u = U/R.y,
         M = length(iMouse.xy)>10. ? iMouse.xy/R : vec2(.5+.5*cos(.5*iTime)); // mouse or auto

    float z =  M.y * ceil (log2(float(textureSize(iChannel0,0).x))); // mouse control LOD
    int iz = int(z);
    vec2 S = vec2(textureSize(iChannel0,iz));                        // size of LOD iz

    V = 2.*U;
    O =  texelFetch( iChannel0, ivec2(V/R*S) , iz );                 // display texture#0 LOD z
    V -= R;
    if (V.x>=0. && V.y >=0.)
        O += texelFetch( iChannel1, ivec2(V/R*S) , iz );             // display texture#1 LOD z



    if (O.a==0.) O += .3;                            // --- display statistics ------------

    U = ( u - vec2(.0,.92) ) * 12.;                  // print resolution
    L(18)L(5)L(19)A(58) spc spc 
    O += pInt( U, R.x ); U.x -= 1.7; L(24); U.x -= .7; O += pInt( U, R.y ); 

                                                     // print LOD level and size
    O += pInt(( u - vec2(.0,.84)) * 12., float(iz));
    U = ( u - vec2(.11 ,.84) ) * 10.; A(58)
    O += pInt(( u - vec2(.23,.83) ) * 10., S.x);
    O += pInt(( u - vec2(.47,.83) ) * 10., S.y);

    vec4 T = texture(iChannel0, U/R, 100.) *R.x*R.y; // sum of whole texture#0
    U = ( u - vec2(-.05,.75) ) * 12.;                // print error
    C(5)L(18)L(18)A(58) spc spc 
    O.r += pFloat( U=( u - vec2(.05,.66) ) * 10., 1e3* (1. - T.r/(R.x*R.y) ) );  U.x -= 4.5; C(5); A(45);A(3+48);
    O.g += pFloat( U=( u - vec2(.05,.58) ) * 10., 1e3* (1. - T.g/(R.x*R.y) ) );  U.x -= 4.5; C(5); A(45);A(3+48);
    O.b += pFloat( U=( u - vec2(.05,.50) ) * 10., 1e3* (1. - T.b/(R.x*R.y) ) );  U.x -= 4.5; C(5); A(45);A(3+48);

    T = texture(iChannel1, U/R, 100.) *R.x*R.y;      // sum of whole texture#1
    V=u; u.x -= .55*R.x/R.y; u.y += .32;
    U = ( u - vec2(-.05,.75) ) * 12.;                // print error
    C(5)L(18)L(18)A(58) spc spc 
    O.r += pFloat( U=( u - vec2(.05,.66) ) * 10., 1e3* (1. - T.r/(R.x*R.y) ) );  U.x -= 4.5; C(5); A(45);A(3+48);
    O.g += pFloat( U=( u - vec2(.05,.58) ) * 10., 1e3* (1. - T.g/(R.x*R.y) ) );  U.x -= 4.5; C(5); A(45);A(3+48);
    O.b += pFloat( U=( u - vec2(.05,.50) ) * 10., 1e3* (1. - T.b/(R.x*R.y) ) );  U.x -= 4.5; C(5); A(45);A(3+48);

                                                     // --- display right slider
    float r = length( V-.95*vec2(R.x,0)/R.y - vec2(0,floor(z)/9.) );
    O += smoothstep( .045, .04, r );// * ( .7+.3*sin(18.*iTime));
    O -= smoothstep( .005, .0, abs(r-.045) );
}



// === text and numbers printing utilities =========================

// --- access to the image of ascii code c
float char(vec2 p, int c) {
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return 0.;
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx(p/16.),dFdy(p/16.) ).x;
}

// --- display int4
float pInt(vec2 p, float n) {
    float v = 0.;
    if (n < 0.) 
        v += char(p - vec2(-.5,0), 45 ),
        n = -n;

    for (float i = 3.; i>=0.; i--) 
        n /=  9.999999, // 10., // for windows :-(
        v += char(p - .5*vec2(i,0), 48+ int(fract(n)*10.) );
    return v;
}

// --- display float4.4
float pFloat(vec2 p, float n) {
    float v = pInt(p,floor(n)); p.x -= 2.;
        v += char(p, 46);      p.x -= .5;
        v += pInt(p,fract(n)*1e4);
    return v;
}

void mainImage( out vec4 O, vec2 U ) {
    vec2 R = iResolution.xy;
    U -= .5;
    O -= O;
    if (U.x==0.)        O.r += R.x;
    if (U.x==R.x/2.-1.) O.g += R.x;
    if (U.x==R.x-1.)    O.b += R.x;
    O.a = 1.;
}

void mainImage( out vec4 O, vec2 U ) {
    vec2 R = iResolution.xy;
    U -= .5;
    O -= O;
    if (U.y==0.)        O.r += R.y;
    if (U.y==R.y/2.-1.) O.g += R.y;
    if (U.y==R.y-1.)    O.b += R.y;
    O.a = 1.;
}
