void mainImage( out vec4 f, vec2 g )
{
	f.xyz = iResolution;
	g = (g+g-f.xy)/f.y;

    g = vec2(atan(g.x,g.y)/3.14159, length(g));
    g.y -= iTime * .25;
    g.x += g.y * .9;

    vec2 k = mod(g + .25, 0.5) - .25;
	g = mod(g, .5) - .25;

	float 
        m = sin(g.y)*g.y/dot(g,g),
        l = sin(k.y)*k.y/dot(k,k);

	f -= f - smoothstep(m,m*2.2,1.) * smoothstep(l,l*2.2,1.);
}
