vec4 rectangle(vec2 uv, float left, float right, float up, float bottom) {
    uv.y += 0.2;
 	vec4 col = vec4(1.0, 1.0, 1.0, 0.0);
    col.a = (step(left, uv.x) - step(right, uv.x)) * (step(up, uv.y) - step(bottom, uv.y));
    return col;
}
float circle(vec2 uv, vec2 pos, float r) {
    uv -= pos;
    float l = length(uv);
    float d = smoothstep(r, r + 0.005, l);
    return d;
}
vec4 up(vec2 uv) {
    vec4 col = vec4(1.0, 0.0, 0.0, 0.0);
    float c = 1.0 - circle(uv, vec2(0.0, 0.1), 0.2);
    c -= 1.0 - circle(uv, vec2(0.0, -0.3), 0.4);
    col.a = clamp(c, 0.0, 1.0);
    return col;
}
vec4 bottom(vec2 uv) {
    vec4 col = vec4(1.0, 1.0, 1.0, 0.0);
    float a = 1.0 - circle(uv, vec2(0.0, 0.1), 0.2);
    float b = 1.0 - circle(uv, vec2(0.0, -0.315), 0.4);
    col.a = a * b;
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 fc = vec4(0.0, 0.0, 0.0, 1.0);
 	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    vec4 u = up(uv);
    vec4 b = bottom(uv);
    vec4 rect = rectangle(uv, -0.02, 0.02, -0.04, 0.1);
    fc = mix(fc, u, u.a);
    fc = mix(fc, b, b.a);
    fc = mix(fc, rect, rect.a);
 	fragColor = fc;
}
